    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
  },
  summaryBar: {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    gap: '2.2rem',
    background: '#ffffff',
