  },
  card: {
    background: '#fff',
    borderRadius: '16px',
    padding: '2rem',
    width: '100%',
    maxWidth: '800px',
    marginBottom: '2rem',
    boxShadow: '0 6px 20px rgba(0,0,0,0.1)',
    transition: 'transform 0.25s ease, box-shadow 0.25s ease',
    cursor: 'default',
  },
  sectionTitle: {
    fontSize: '1.35rem',
    fontWeight: 600,
    marginBottom: '1rem',
    color: '#1f2937',
    borderBottom: '1px solid #e5e7eb',
    paddingBottom: '0.6rem',
  },
  button: {
    marginTop: '1.2rem',
    padding: '0.7rem 1.4rem',
    background: '#1e3a8a',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 600,
    cursor: 'pointer',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease',
  },
  listItem: {
    padding: '0.6rem 0',
    borderBottom: '1px solid #e5e7eb',
    fontSize: '0.95rem',
    color: '#374151',
  },
  listEmpty: {
    color: '#6b7280',
    fontStyle: 'italic',
    fontSize: '0.95rem',
    padding: '0.5rem 0',
  },
  list: { listStyle: 'none', padding: 0, margin: 0 },
};

export default TenantDashboard;
