import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const LandlordDashboard = ({ user, onLogout }) => {
  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    if (!user?.id) return;
    fetchData();
  }, [user]);

  const fetchData = async () => {
    try {
      const [propsRes, tenantsRes, paymentsRes, maintRes] = await Promise.all([
        api.get(`/landlord/properties/${user.id}`),
        api.get(`/landlord/tenants/${user.id}`),
        api.get(`/landlord/payments/${user.id}`),
        api.get(`/landlord/maintenance/${user.id}`)
      ]);
      setProperties(propsRes.data);
      setTenants(tenantsRes.data);
      setPayments(paymentsRes.data);
      setMaintenance(maintRes.data);
    } catch (err) {
      console.error("‚ùå Error loading landlord data:", err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  // üî¢ Quick stats calculations
  const totalProperties = properties.length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);

  // üõ†Ô∏è Update maintenance status
  const updateMaintenanceStatus = async (id, status) => {
    try {
      await api.patch(`/landlord/maintenance/${id}`, { status });
      // Refresh list after update
      const res = await api.get(`/landlord/maintenance/${user.id}`);
      setMaintenance(res.data);
    } catch (err) {
      console.error(`‚ùå Failed to update maintenance ${id}:`, err.response?.data || err.message);
    }
  };

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* ‚úÖ Quick Stats Bar */}
        <div style={styles.statsBar}>
          <div style={styles.stat}><strong>üè† Properties:</strong> {totalProperties}</div>
          <div style={styles.stat}><strong>üë• Tenants:</strong> {totalTenants}</div>
          <div style={styles.stat}><strong>üí≥ Rent Collected:</strong> ${totalRentCollected.toFixed(2)}</div>
        </div>

        <div style={styles.grid}>
          {/* Properties */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üè† Properties</h3>
            {properties.length === 0 ? (
              <p>No properties found.</p>
            ) : (
              <ul style={styles.list}>
                {properties.map(p => (
                  <li key={p.property_id}>{p.address} ‚Äî {p.city}, {p.state}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Tenants */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üë• Tenants</h3>
            {tenants.length === 0 ? (
              <p>No tenants assigned.</p>
            ) : (
              <ul style={styles.list}>
                {tenants.map(t => (
                  <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Payments */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üí≥ Payments</h3>
            {payments.length === 0 ? (
              <p>No payments recorded.</p>
            ) : (
              <ul style={styles.list}>
                {payments.map(pay => (
                  <li key={pay.payment_id}>
                    {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Maintenance Requests */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üõ†Ô∏è Maintenance Requests</h3>
            {maintenance.length === 0 ? (
              <p>No maintenance requests yet.</p>
            ) : (
              <ul style={styles.list}>
                {maintenance.map(m => (
                  <li key={m.request_id} style={{ marginBottom: '0.5rem' }}>
                    <strong>{m.type}:</strong> {m.description} ‚Äî 
                    Status: {m.status} ‚Äî 
                    {new Date(m.created_at).toLocaleDateString()}
                    <div style={{ marginTop: '0.5rem' }}>
                      {m.status !== 'approved' && (
                        <button style={styles.button} onClick={() => updateMaintenanceStatus(m.request_id, 'approved')}>
                          Approve
                        </button>
                      )}
                      {m.status !== 'completed' && (
                        <button style={styles.buttonSecondary} onClick={() => updateMaintenanceStatus(m.request_id, 'completed')}>
                          Complete
                        </button>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif'
  },
  navbar: {
    background: '#2563eb',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    position: 'sticky',
    top: 0,
    zIndex: 1000
  },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: {
    background: 'transparent',
    border: '1px solid #fff',
    color: '#fff',
    cursor: 'pointer',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    transition: 'background 0.2s ease'
  },
  logoutButton: {
    background: '#dc3545',
    border: 'none',
    color: '#fff',
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.2s ease'
  },
  container: { padding: '2rem', maxWidth: '1200px', margin: '0 auto' },
  heading: { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' },
  role: { fontSize: '1rem', marginBottom: '2rem', color: '#666' },
  statsBar: {
    display: 'flex',
    justifyContent: 'space-around',
    backgroundColor: '#f3f4f6',
    padding: '1rem',
    borderRadius: '10px',
    marginBottom: '2rem',
    boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
  },
  stat: { fontSize: '1.1rem', color: '#333' },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '1.5rem'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
    padding: '1.5rem',
    transition: 'transform 0.2s ease, box-shadow 0.2s ease'
  },
  sectionTitle: {
    fontSize: '1.25rem',
    marginBottom: '1rem',
    color: '#333',
    borderBottom: '1px solid #ddd',
    paddingBottom: '0.5rem'
  },
  list: { listStyle: 'none', padding: 0, margin: 0 },
  button: {
    backgroundColor: '#0078D4',
    color: '#fff',
    border: 'none',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    cursor: 'pointer',
    marginRight: '0.5rem',
    transition: 'background 0.2s ease'
  },
  buttonSecondary: {
    backgroundColor: '#6c757d',
    color: '#fff',
    border: 'none',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
