import React from 'react';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';

const TenantDashboard = ({ user }) => {
  const handlePaymentClick = async () => {
    console.log('üß™ Button clicked');
    try {
      const token = localStorage.getItem('token'); // ‚úÖ get token
      const res = await api.post('/payments/create-session', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log('üîÅ Stripe session response:', res.data);
      if (res.data?.url) {
        console.log('üåê Redirecting to Stripe:', res.data.url);
        window.location.href = res.data.url;
      } else {
        console.error('‚ùå No Stripe URL returned:', res.data);
      }
    } catch (err) {
      console.error('‚ùå Payment error:', err.response?.data || err.message);
    }
  };

  const containerStyle = { padding: '2rem', fontFamily: 'Segoe UI, sans-serif', backgroundColor: '#f0f2f5', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center' };
  const headingStyle = { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' };
  const roleStyle = { fontSize: '1rem', marginBottom: '2rem', color: '#666' };
  const cardStyle = { backgroundColor: '#fff', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', padding: '1.5rem', width: '100%', maxWidth: '600px', marginBottom: '2rem' };
  const sectionTitle = { fontSize: '1.25rem', marginBottom: '1rem', color: '#333', borderBottom: '1px solid #ddd', paddingBottom: '0.5rem' };
  const buttonStyle = { marginTop: '1rem', padding: '0.5rem 1rem', backgroundColor: '#0078D4', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Welcome, {user.name}</h2>
      <p style={roleStyle}>Role: tenant</p>

      <div style={cardStyle}>
        <h3 style={sectionTitle}>Tenant Profile</h3>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
      </div>

      <div style={cardStyle}>
        <h3 style={sectionTitle}>Lease Info</h3>
        <TenantLeaseCard userId={user.id} />
      </div>

      <div style={cardStyle}>
        <h3 style={sectionTitle}>Payment History</h3>
        <TenantPayments userId={user.id} />
        <button style={buttonStyle} onClick={handlePaymentClick}>
          Make a Payment
        </button>
      </div>
    </div>
  );
};

export default TenantDashboard;
