import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ userId }) => {
  const [lease, setLease] = useState(null);

  useEffect(() => {
    const fetchLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/leases', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const userLease = res.data.find(l => l.tenant_id === userId);
        setLease(userLease || null);
      } catch (err) {
        console.error('Lease fetch error:', err);
      }
    };
    fetchLease();
  }, [userId]);

  return (
    <div style={cardStyle}>
      {lease ? (
  <>
    <p><strong>Name:</strong> {lease.tenant?.name || 'Unknown'}</p>
    <p><strong>Email:</strong> {lease.tenant?.email || 'Unknown'}</p>
    <p><strong>Property ID:</strong> {lease.property_id}</p>
    <p><strong>Rent:</strong> {lease.rent_amount > 0 ? `$${lease.rent_amount}` : 'Not set'}</p>
    <p><strong>Start:</strong> {new Date(lease.start_date).toLocaleDateString()}</p>
    <p><strong>End:</strong> {new Date(lease.end_date).toLocaleDateString()}</p>
    <p><strong>Status:</strong> {lease.status || 'N/A'}</p>
  </>
) : (
  <p>No lease found.</p>
)}
    </div>
  );
};


const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff',
  marginBottom: '2rem'
};

export default TenantLeaseCard;
