import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  // logout
  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  }, [navigate, onLogout]);

  // load user + reminders
  useEffect(() => {
    const stored = localStorage.getItem('user');
    if (stored) {
      const parsed = JSON.parse(stored);
      setUser(parsed);
      if (parsed.role !== 'tenant') navigate('/unauthorized');

      api.get(`/user-reminders/${parsed.user_id}`)
        .then((res) => setReminders(res.data))
        .catch((err) => {
          const msg = err.response?.data?.message;
          if (msg === 'Token expired') handleLogout();
          else console.error("‚ùå Error loading reminders:", msg || err.message);
        });
    } else {
      navigate('/login');
    }
  }, [navigate, handleLogout]);

  // load lease
  useEffect(() => {
    if (!user?.user_id) return;

    const loadLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get(`/leases/user/${user.user_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // ‚≠ê FIX: ensure missing fields don't break UI
        const data = res.data.lease || res.data;

        setLease({
          rent_amount: data.rent_amount ?? 0,
          renewal_requested: data.renewal_requested ?? 0,
          ...data
        });

      } catch (err) {
        const msg = err.response?.data?.message;
        if (msg === 'Token expired') handleLogout();
        else console.error("‚ùå Error loading lease:", msg || err.message);
      }
    };

    loadLease();
  }, [user, handleLogout]);

  // load passcodes
  useEffect(() => {
    if (lease?.lease_id) {
      api.get(`/smart-passcodes/${lease.lease_id}`)
        .then((res) => setPasscodes(res.data))
        .catch((err) => {
          const msg = err.response?.data?.message;
          if (msg === 'Token expired') handleLogout();
          else console.error("‚ùå Error loading passcodes:", msg || err.message);
        });
    }
  }, [lease, handleLogout]);

  // payment handler
  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post(
        '/payments/create-session',
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === 'Token expired') handleLogout();
      else console.error('‚ùå Payment error:', msg || err.message);
    }
  };

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Tenant Portal</div>
        <div style={styles.navRight}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>Profile</button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>Dashboard</button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {lease && (
        <div style={styles.summaryBar}>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Next Rent Due</span>
            <span style={styles.summaryValue}>
              {parseFloat(lease.rent_amount) > 0
                ? `$${parseFloat(lease.rent_amount).toFixed(2)}`
                : 'Not set'}
            </span>
          </div>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Lease Ends</span>
            <span style={styles.summaryValue}>
              {lease.end_date
                ? new Date(lease.end_date).toLocaleDateString()
                : '‚Äî'}
            </span>
          </div>
        </div>
      )}

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name || 'Guest'}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
        </div>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Lease Info</h3>
          <TenantLeaseCard lease={lease} user={user} />
        </div>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Payment History</h3>
          <TenantPayments userId={user?.user_id} />
          <button style={styles.button} onClick={handlePaymentClick}>Make a Payment</button>
        </div>

        {lease && (
          <>
            <div style={styles.card}>
              <h3 style={styles.sectionTitle}>üì∑ Lease Photos</h3>
              <LeasePhotosForm leaseId={lease.lease_id} token={localStorage.getItem('token')} role={user?.role} />
            </div>

            <div style={styles.card}>
              <h3 style={styles.sectionTitle}>üìÑ Lease Documents</h3>
              <DocumentsForm leaseId={lease.lease_id} userId={user?.user_id} role={user?.role} />
            </div>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  page: {
    backgroundColor: '#f0f2f5',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif'
  },
  navbar: {
    background: '#0078D4',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem'
  },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: { background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer' },
  logoutButton: {
    background: '#dc3545',
    border: 'none',
    color: '#fff',
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    cursor: 'pointer'
  },
  summaryBar: {
    display: 'flex',
    justifyContent: 'center',
    gap: '2rem',
    background: '#fff',
    padding: '1rem 2rem',
    margin: '1rem auto',
    borderRadius: '8px',
    maxWidth: '700px'
  },
  summaryItem: { display: 'flex', flexDirection: 'column', alignItems: 'center' },
  summaryLabel: { fontSize: '0.9rem', color: '#666' },
  summaryValue: { fontSize: '1.2rem', fontWeight: 'bold', color: '#0078D4' },
  container: { padding: '2rem', display: 'flex', flexDirection: 'column', alignItems: 'center' },
  heading: { fontSize: '2rem' },
  role: { fontSize: '1rem', marginBottom: '2rem', color: '#666' },
  card: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    padding: '2rem',
    width: '100%',
    maxWidth: '700px',
    marginBottom: '2rem'
  },
