import React, { useState } from 'react';
import axios from 'axios';

const MaintenanceForm = ({ onCreate, user }) => {
  const [formData, setFormData] = useState({
    property_id: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/maintenance', {
        ...formData,
        user_id: user?.user_id,
        status: 'open'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreate(res.data);
      setFormData({ property_id: '', description: '' });
    } catch (err) {
      console.error('‚ùå Create error:', err);
    }
  };

  // Only tenants should see the form
  if (user?.role !== 'tenant') return null;

  return (
    <form onSubmit={handleSubmit} style={formContainer}>
      <h3 style={formTitle}>Submit a Maintenance Request</h3>

      <div style={formGroup}>
        <label style={label}>Property ID</label>
        <input
          type="text"
          name="property_id"
          value={formData.property_id}
          onChange={handleChange}
          style={input}
          required
        />
      </div>

      <div style={formGroup}>
        <label style={label}>Description</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          style={textarea}
          required
        />
      </div>

      <button type="submit" style={submitButton}>Submit Request</button>
    </form>
  );
};

/* üé® Styles */
const formContainer = {
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem'
};

const formTitle = {
  fontSize: '1.2rem',
  fontWeight: '600',
  marginBottom: '0.5rem'
};

const formGroup = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.25rem'
};

const label = {
  fontSize: '0.9rem',
  fontWeight: '500'
};

const input = {
  padding: '0.5rem',
  border: '1px solid #ccc',
  borderRadius: '4px'
};

const textarea = {
  padding: '0.5rem',
  border: '1px solid #ccc',
  borderRadius: '4px',
  minHeight: '80px'
};

const submitButton = {
  padding: '0.6rem 1.2rem',
  border: 'none',
  borderRadius: '4px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease'
};

export default MaintenanceForm;
