import React, { useState } from 'react';
import axios from 'axios';

const TenantForm = ({ onCreate }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    role: 'tenant', // force tenant role
    password: ''    // backend usually requires password for new users
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/users', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreate(res.data); // push new tenant into parent state
      setFormData({ name: '', email: '', phone: '', role: 'tenant', password: '' });
      alert(`✅ Tenant ${res.data.id} created successfully`);
    } catch (err) {
      console.error('❌ Create tenant error:', err);
      alert(err.response?.data?.error || 'Failed to create tenant');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h3 style={headingStyle}>➕ Add New Tenant</h3>
      <input
        name="name"
        placeholder="Full Name"
        value={formData.name}
        onChange={handleChange}
        required
        style={inputStyle}
      />
      <input
        name="email"
        type="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
        style={inputStyle}
      />
      <input
        name="phone"
        placeholder="Phone Number"
        value={formData.phone}
        onChange={handleChange}
        style={inputStyle}
      />
      <input
        name="password"
        type="password"
        placeholder="Temporary Password"
        value={formData.password}
        onChange={handleChange}
        required
        style={inputStyle}
      />
      <button type="submit" style={buttonStyle}>Create Tenant</button>
    </form>
  );
};

/* Styles */
const formStyle = { display: 'flex', flexDirection: 'column', gap: '1rem', padding: '1.5rem', backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', maxWidth: '400px', margin: '0 auto' };
const headingStyle = { fontSize: '1.5rem', marginBottom: '1rem', color: '#0078D4', textAlign: 'center' };
const inputStyle = { padding: '0.75rem', borderRadius: '6px', border: '1px solid #ccc', fontSize: '1rem' };
const buttonStyle = { padding: '0.75rem', fontSize: '1rem', borderRadius: '6px', border: 'none', backgroundColor: '#0078D4', color: '#fff', cursor: 'pointer' };

export default TenantForm;
