import React from 'react';

const TenantLeaseCard = ({ lease, user }) => {
  if (!lease) return <p>Loading lease...</p>;

  const rent = lease?.rent_amount;
  const rentDisplay =
    rent && !isNaN(parseFloat(rent)) && parseFloat(rent) > 0
      ? `$${parseFloat(rent).toFixed(2)}`
      : 'Not set';

  return (
    <div style={cardStyle}>
      <p><strong>Name:</strong> {user?.name || 'N/A'}</p>
      <p><strong>Email:</strong> {user?.email || 'N/A'}</p>

      <p><strong>Lease ID:</strong> {lease.lease_id || 'N/A'}</p>
      <p><strong>Property ID:</strong> {lease.property_id || 'N/A'}</p>

      <p><strong>Rent:</strong> {rentDisplay}</p>

      <p><strong>Start:</strong> 
        {lease.start_date
          ? new Date(lease.start_date).toLocaleDateString()
          : 'N/A'}
      </p>

      <p><strong>End:</strong> 
        {lease.end_date
          ? new Date(lease.end_date).toLocaleDateString()
          : 'N/A'}
      </p>

      <p>
        <strong>Status:</strong>{' '}
        {lease.renewal_requested === 1
          ? <span style={badgeRenewal}>Renewal requested</span>
          : <span style={badgeActive}>Active</span>}
      </p>

      <p>
        <strong>Lease File:</strong>{' '}
        {lease.lease_file_url ? (
          <a
            href={`http://localhost:5000/uploads/${lease.lease_file_url}`}
            target="_blank"
            rel="noreferrer"
          >
            View File
          </a>
        ) : (
          'Not uploaded'
        )}
      </p>
    </div>
  );
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff',
  marginBottom: '2rem',
};

const badgeActive = {
  backgroundColor: '#28a745',
  color: '#fff',
  padding: '0.25rem 0.5rem',
  borderRadius: '4px',
};

const badgeRenewal = {
  backgroundColor: '#ffc107',
  color: '#000',
  padding: '0.25rem 0.5rem',
  borderRadius: '4px',
};

export default TenantLeaseCard;
