import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SmartPasscodeForm from '../components/SmartPasscodeForm';
import SmartPasscode from '../components/SmartPasscode';

const PasscodesPage = ({ user }) => {
  const [leases, setLeases] = useState([]);
  const [users, setUsers] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const leaseRes = await axios.get('/api/leases', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setLeases(leaseRes.data);

        const userRes = await axios.get('/api/users', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setUsers(userRes.data);

        const passRes = await axios.get('/api/smart-passcodes/1', { // demo: lease_id=1
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setPasscodes(passRes.data);
      } catch (err) {
        console.error('‚ùå Fetch error:', err);
      }
    };
    fetchData();
  }, []);

  const handleCreated = (newCode) => setPasscodes(prev => [...prev, newCode]);

  const handleRevoke = async (id) => {
    try {
      await axios.patch(`/api/smart-passcodes/${id}/revoke`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPasscodes(prev => prev.map(pc => pc.passcode_id === id ? { ...pc, is_active: 0 } : pc));
    } catch (err) {
      console.error('‚ùå Revoke error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/smart-passcodes/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPasscodes(prev => prev.filter(pc => pc.passcode_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
    }
  };

  return (
    <div style={page}>
      <div style={header}>
        <span style={icon}>üîê</span>
        <h2 style={title}>Smart Passcodes</h2>
      </div>

      {['admin','landlord'].includes(user?.role) && (
        <SmartPasscodeForm leases={leases} users={users} onCreated={handleCreated} />
      )}

      <div style={list}>
        <h3>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p style={{ color: '#666' }}>No passcodes yet.</p>
        ) : (
          passcodes.map(pc => (
            <SmartPasscode
              key={pc.passcode_id}
              passcode={pc}
              onRevoke={handleRevoke}
              onDelete={handleDelete}
            />
          ))
        )}
      </div>
    </div>
  );
};

/* üé® Styles */
const page = { maxWidth: '900px', margin: '2rem auto', padding: '2rem', background: '#f9f9f9', borderRadius: '10px' };
const header = { display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '1.5rem', borderBottom: '2px solid #eee', paddingBottom: '0.5rem' };
const icon = { fontSize: '1.8rem' };
const title = { fontSize: '1.6rem', fontWeight: '600', margin: 0 };
const list = { marginTop: '2rem' };

export default PasscodesPage;
