import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import PropertyList from '../components/PropertyList';
import PropertyForm from '../components/PropertyForm';

const LandlordDashboard = ({ user, onLogout }) => {
  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    if (!user?.id) return;

    Promise.all([
      api.get(`/properties/user?user_id=${user.id}`),
      api.get(`/leases/landlord-tenants/${user.id}`),
      api.get(`/payments/landlord/${user.id}`),
      api.get(`/maintenance/landlord/${user.id}`)
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord data:", err.response?.data || err.message)
      );
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  const handleCreate = (newProp) => {
    setProperties(prev => [newProp, ...prev]);
    setShowForm(false);
  };

  const handleDelete = (id) => {
    setProperties(prev => prev.filter(p => p.property_id !== id));
  };

  const handleUpdate = (updatedProp) => {
    setProperties(prev =>
      prev.map(p => (p.property_id === updatedProp.property_id ? updatedProp : p))
    );
  };

  const totalProperties = properties.length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);

  const updateMaintenanceStatus = async (id, status) => {
    try {
      await api.patch(`/landlord/maintenance/${id}`, { status });
      const res = await api.get(`/landlord/maintenance/${user.id}`);
      setMaintenance(res.data);
    } catch (err) {
      console.error(`‚ùå Failed to update maintenance ${id}:`, err.response?.data || err.message);
    }
  };

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        <div style={styles.statsBar}>
          <div style={styles.stat}><strong>üè† Properties:</strong> {totalProperties}</div>
          <div style={styles.stat}><strong>üë• Tenants:</strong> {totalTenants}</div>
          <div style={styles.stat}><strong>üí≥ Rent Collected:</strong> ${totalRentCollected.toFixed(2)}</div>
        </div>

        <div style={styles.grid}>
          {/* Properties */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üè† Properties</h3>
            <PropertyList
              properties={properties}
              onDelete={handleDelete}
              onUpdate={handleUpdate}
              user={user}
            />
            <button style={styles.button} onClick={() => setShowForm(true)}>Ôºã Add Property</button>
            {showForm && (
              <div style={styles.modalOverlay}>
                <div style={styles.modalContent}>
                  <PropertyForm onCreate={handleCreate} user={user} />
                  <button style={styles.closeButton} onClick={() => setShowForm(false)}>Close</button>
                </div>
              </div>
            )}
          </div>

          {/* Tenants */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üë• Tenants</h3>
            {tenants.length === 0 ? (
              <p>No tenants assigned.</p>
            ) : (
              <ul style={styles.list}>
                {tenants.map(t => (
                  <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Payments */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üí≥ Payments</h3>
            {payments.length === 0 ? (
              <p>No payments recorded.</p>
            ) : (
              <ul style={styles.list}>
                {payments.map(pay => (
                  <li key={pay.payment_id}>
                    {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Maintenance Requests */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üõ†Ô∏è Maintenance Requests</h3>
            {maintenance.length === 0 ? (
              <p>No maintenance requests yet.</p>
            ) : (
              <ul style={styles.list}>
                {maintenance.map(m => (
                  <li key={m.request_id} style={{ marginBottom: '0.5rem' }}>
                    <strong>{m.type}:</strong> {m.description} ‚Äî 
                    Status: {m.status} ‚Äî 
                    {new Date(m.created_at).toLocaleDateString()}
                    <div style={{ marginTop: '0.5rem' }}>
                      {m.status !== 'approved' && (
                        <button style={styles.button} onClick={() => updateMaintenanceStatus(m.request_id, 'approved')}>
                          Approve
                        </button>
                      )}
                      {m.status !== 'completed' && (
                        <button style={styles.buttonSecondary} onClick={() => updateMaintenanceStatus(m.request_id, 'completed')}>
                          Complete
                        </button>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  /* keep your styles as before */
};

export default LandlordDashboard;
