import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function LandlordProperties() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || user.role?.toLowerCase() !== 'landlord') {
      navigate('/unauthorized');
      return;
    }

    api.get('/properties/landlord', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
      .then(res => setProperties(res.data))
      .catch(err =>
        console.error("‚ùå Error loading landlord properties:", err.response?.data || err.message)
      )
      .finally(() => setLoading(false));
  }, [user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (loading) {
    return <p style={loadingStyle}>Loading properties...</p>;
  }

  return (
    <div style={pageStyle}>
      <nav style={navStyle}>
        <div style={navLeft}>üè† Landlord Portal</div>
        <div style={navRight}>
          <button style={navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={containerStyle}>
        <h2 style={headingStyle}>All Properties</h2>

        {properties.length === 0 ? (
          <p>No properties found.</p>
        ) : (
          <div style={cardGrid}>
            {properties.map(p => (
              <div
                key={p.property_id}
                style={{
                  ...card,
                  border: p.is_owned ? '2px solid #2563eb' : '1px solid #ccc',
                  backgroundColor: p.is_owned ? '#e0f2fe' : '#fff'
                }}
              >
                <h3 style={cardTitle}>{p.address} ‚Äî {p.city}, {p.state}</h3>
                <p>Status: <span style={status}>{p.status}</span></p>
                <p>Rent Amount: ${p.rent_amount}</p>
                <p>Tenants: {p.tenant_count || 0}</p>
                <p>Rent Collected: ${p.rent_collected || 0}</p>
                <p>Open Requests: {p.open_requests || 0}</p>
                {p.is_owned ? <p style={{ fontWeight: 'bold', color: '#2563eb' }}>‚úÖ Your Property</p> : null}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
