// TenantLeaseCard.jsx
import React from 'react';
import PropTypes from 'prop-types';
import { format } from 'date-fns';

const TenantLeaseCard = ({ lease, onViewPayments, onUploadDocs }) => {
  if (!lease) return <p>Loading lease...</p>;

  const {
    lease_id,
    property_name,
    tenant_name,
    start_date,
    end_date,
    rent_amount,
    status,
  } = lease;

  return (
    <div style={styles.card}>
      <div style={styles.header}>
        <h4 style={styles.property}>{property_name}</h4>
        <span style={{...styles.status, backgroundColor: status === 'Active' ? '#d4edda' : '#f8f9fa', color: status === 'Active' ? '#155724' : '#6c757d'}}>
          {status}
        </span>
      </div>

      <p><strong>Tenant:</strong> {tenant_name}</p>
      <p>
        <strong>Lease Period:</strong>{' '}
        {format(new Date(start_date), 'MMM dd, yyyy')} - {format(new Date(end_date), 'MMM dd, yyyy')}
      </p>
      <p><strong>Rent:</strong> ${rent_amount?.toLocaleString() || 'Not set'}</p>

      <div style={styles.buttons}>
        {onViewPayments && (
          <button style={styles.buttonBlue} onClick={() => onViewPayments(lease_id)}>
            View Payments
          </button>
        )}
        {onUploadDocs && (
          <button style={styles.buttonGreen} onClick={() => onUploadDocs(lease_id)}>
            Upload Docs
          </button>
        )}
      </div>
    </div>
  );
};

TenantLeaseCard.propTypes = {
  lease: PropTypes.object.isRequired,
  onViewPayments: PropTypes.func,
  onUploadDocs: PropTypes.func,
};

const styles = {
  card: { background: '#fff', padding: '1rem', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', marginBottom: '1rem' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.5rem' },
  property: { fontSize: '1.1rem', fontWeight: '600' },
  status: { padding: '0.2rem 0.5rem', borderRadius: '4px', fontSize: '0.85rem', fontWeight: '500' },
  buttons: { display: 'flex', gap: '0.5rem', marginTop: '1rem' },
  buttonBlue: { backgroundColor: '#0078D4', color: '#fff', border: 'none', padding: '0.4rem 0.8rem', borderRadius: '4px', cursor: 'pointer' },
  buttonGreen: { backgroundColor: '#28a745', color: '#fff', border: 'none', padding: '0.4rem 0.8rem', borderRadius: '4px', cursor: 'pointer' },
};

export default TenantLeaseCard;
