import { useNavigate } from 'react-router-dom';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';

export default function Profile({ user }) {
  const navigate = useNavigate();

  if (!user) {
    return (
      <div style={{ padding: '2rem', fontFamily: 'Segoe UI, sans-serif' }}>
        <h2>Tenant Profile</h2>
        <p>No user data found.</p>
      </div>
    );
  }

  const userId = user?.id ?? user?.user_id;

  const containerStyle = {
    padding: '2rem',
    fontFamily: 'Segoe UI, sans-serif',
    backgroundColor: '#f0f2f5',
    minHeight: '100vh'
  };

  const cardStyle = {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    padding: '1.5rem',
    width: '100%',
    maxWidth: '600px',
    marginBottom: '2rem'
  };

  const headingStyle = {
    fontSize: '2rem',
    marginBottom: '1rem',
    color: '#222'
  };

  const sectionTitle = {
    fontSize: '1.25rem',
    marginBottom: '1rem',
    color: '#333',
    borderBottom: '1px solid #ddd',
    paddingBottom: '0.5rem'
  };

  const buttonStyle = {
    marginTop: '1rem',
    padding: '0.5rem 1rem',
    backgroundColor: '#0078D4',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  const backButtonStyle = {
    marginTop: '1rem',
    background: 'transparent',
    border: 'none',
    color: '#0078D4',
    cursor: 'pointer'
  };

  return (
    <div style={containerStyle}>
      <div style={cardStyle}>
        <h2 style={headingStyle}>Tenant Profile</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
      </div>

      <div style={cardStyle}>
        <h3 style={sectionTitle}>Lease Info</h3>
        <TenantLeaseCard userId={userId} />
      </div>

      <div style={cardStyle}>
        <h3 style={sectionTitle}>Payment History</h3>
        <TenantPayments userId={userId} />
        {(user.role === 'tenant' || user.role === 'landlord') && (
          <button style={buttonStyle} onClick={() => navigate('/payments')}>
            Make a Payment
          </button>
        )}
      </div>

      <button style={backButtonStyle} onClick={() => navigate('/tenant')}>
        ‚Üê Back to Dashboard
      </button>
    </div>
  );
}
