import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MaintenanceForm from '../components/MaintenanceForm';
import MaintenanceList from '../components/MaintenanceList';

axios.defaults.baseURL = 'http://localhost:5000';

const MaintenancePage = ({ user }) => {
  const [requests, setRequests] = useState([]);
  const [filter, setFilter] = useState('all');
  const [search, setSearch] = useState('');

  const fetchRequests = async () => {
    try {
      const res = await axios.get('/api/maintenance', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(res.data);
    } catch (err) {
      console.error('‚ùå Fetch error:', err);
    }
  };

  const handleCreate = (newRequest) => {
    setRequests(prev => [...prev, newRequest]);
  };

  const handleUpdate = async (id, data) => {
    try {
      await axios.patch(`/api/maintenance/${id}`, data, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(prev =>
        prev.map(r => (r.request_id === id ? { ...r, ...data } : r))
      );
    } catch (err) {
      console.error('‚ùå Update error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/maintenance/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(prev => prev.filter(r => r.request_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  const filteredRequests = requests.filter(r => {
    const matchesFilter = filter === 'all' || r.status === filter;
    const matchesSearch =
      r.description.toLowerCase().includes(search.toLowerCase()) ||
      String(r.property_id).includes(search);
    return matchesFilter && matchesSearch;
  });

  return (
    <div style={pageContainer}>
      {/* Header */}
      <div style={header}>
        <span style={headerIcon}>üõ†Ô∏è</span>
        <h2 style={headerTitle}>Maintenance Requests</h2>
      </div>

      {/* Filter + Search */}
      {['admin','landlord'].includes(user?.role) && (
        <div style={filterRow}>
          <button style={filterButton} onClick={() => setFilter('all')}>All</button>
          <button style={filterButton} onClick={() => setFilter('open')}>Open</button>
          <button style={filterButton} onClick={() => setFilter('in_progress')}>In Progress</button>
          <button style={filterButton} onClick={() => setFilter('closed')}>Closed</button>
          <input
            type="text"
            placeholder="Search by description or property ID"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={searchBox}
          />
        </div>
      )}

      {/* Form */}
      {user?.role === 'tenant' && (
        <div style={card}>
          <MaintenanceForm onCreate={handleCreate} user={user} />
        </div>
      )}

      {/* List */}
      <div style={card}>
        <MaintenanceList
          requests={filteredRequests}
          onUpdate={handleUpdate}
          onDelete={handleDelete}
          user={user}
        />
      </div>
    </div>
  );
};

/* üé® Styles */
const pageContainer = {
  maxWidth: '900px',
  margin: '2rem auto',
  padding: '2rem',
  backgroundColor: '#f9f9f9',
  borderRadius: '10px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
};

const header = {
  display: 'flex',
  alignItems: 'center',
  gap: '0.75rem',
  marginBottom: '1.5rem',
  borderBottom: '2px solid #eee',
  paddingBottom: '0.5rem'
};

const headerIcon = { fontSize: '1.8rem' };
const headerTitle = { fontSize: '1.8rem', fontWeight: '600', margin: 0 };

const filterRow = {
  display: 'flex',
  flexWrap: 'wrap',
  gap: '0.75rem',
  marginBottom: '1.5rem'
};

const filterButton = {
  padding: '0.5rem 1rem',
  border: 'none',
  borderRadius: '4px',
  backgroundColor: '#3498db',
  color: '#fff',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease'
};

const searchBox = {
  flex: 1,
  minWidth: '200px',
  padding: '0.5rem',
  border: '1px solid #ccc',
  borderRadius: '4px'
};

const card = {
  backgroundColor: '#fff',
  borderRadius: '8px',
  padding: '1rem',
  marginBottom: '1.5rem',
  boxShadow: '0 1px 4px rgba(0,0,0,0.08)'
};


export default MaintenancePage;
