import React, { useEffect, useState } from 'react';
import api from '../services/api';

const PaymentsPage = () => {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('‚ùå No token found, user not authenticated');
          setLoading(false);
          return;
        }

        const res = await api.get('/payments/user', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPayments(res.data);
      } catch (err) {
        console.error('‚ùå Failed to fetch payments:', err.response?.data || err.message);
        setError(err.response?.data?.error || 'Failed to fetch payments.');
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  const handleStripeCheckout = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('‚ùå No token found, cannot initiate Stripe checkout');
        return;
      }

      const res = await api.post('/payments/create-session', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data?.url) {
        window.location.href = res.data.url; // redirect to Stripe
      } else {
        setError('‚ùå No Stripe URL returned.');
      }
    } catch (err) {
      console.error("Stripe session error:", err.response?.data || err.message);
      setError(err.response?.data?.error || 'Stripe session error.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üí≥ Payments</h2>

      {loading ? (
        <p>Loading payments...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : payments.length === 0 ? (
        <p>No payments found.</p>
      ) : (
        <ul>
          {payments.map((p, index) => (
            <li key={p.payment_id || index}>
              <strong>Amount:</strong>{' '}
              {new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(p.amount)} ‚Äî{' '}
              <strong>Date:</strong> {new Date(p.paid_date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}

      <button
        onClick={handleStripeCheckout}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          background: "black",
          color: "white",
          cursor: "pointer",
          borderRadius: "6px",
          fontWeight: "600"
        }}
      >
        Pay Now with Stripe
      </button>
    </div>
  );
};

export default PaymentsPage;
