import React, { useEffect, useState } from 'react';
import api from '../services/api';

const PaymentsPage = () => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get('/payments/user', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPayments(res.data);
      } catch (err) {
        console.error('‚ùå Failed to fetch payments:', err.response?.data || err.message);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üí≥ Payments</h2>
      {payments.length === 0 ? (
        <p>No payments found.</p>
      ) : (
        <ul>
          {payments.map((p, index) => (
            <li key={p.payment_id || index}>
              <strong>Amount:</strong> ${p.amount} ‚Äî{' '}
              <strong>Date:</strong> {new Date(p.paid_date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PaymentsPage;
