import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SmartPasscodeForm from '../components/SmartPasscodeForm';
import SmartPasscode from '../components/SmartPasscode';

const PasscodesPage = ({ user }) => {
  const [leases, setLeases] = useState([]);
  const [users, setUsers] = useState([]);
  const [passcodes, setPasscodes] = useState([]);
  const [selectedLease, setSelectedLease] = useState(user?.lease_id || '');

  useEffect(() => {
  const fetchData = async () => {
    try {
      const leaseRes = await axios.get('/api/leases', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setLeases(leaseRes.data);

      const userRes = await axios.get('/api/users', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setUsers(userRes.data);

      if (!selectedLease && ['admin', 'landlord'].includes(user?.role)) {
        const firstLease = leaseRes.data[0]?.lease_id || '';
        setSelectedLease(firstLease);
      }

      if (selectedLease) {
        const passRes = await axios.get(`/api/smart-passcodes/${selectedLease}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setPasscodes(passRes.data);
      } else {
        setPasscodes([]);
      }
    } catch (err) {
      console.error('‚ùå Fetch error:', err);
    }
  };
  fetchData();
}, [selectedLease, user?.role]);

  const handleCreated = (newCode) => setPasscodes(prev => [...prev, newCode]);

  const handleRevoke = async (id) => {
    try {
      await axios.patch(`/api/smart-passcodes/${id}/revoke`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPasscodes(prev =>
        prev.map(pc => pc.passcode_id === id ? { ...pc, is_active: 0 } : pc)
      );
    } catch (err) {
      console.error('‚ùå Revoke error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/smart-passcodes/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPasscodes(prev => prev.filter(pc => pc.passcode_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
    }
  };

  return (
    <div style={page}>
      <div style={header}>
        <span style={icon}>üîê</span>
        <h2 style={title}>Smart Passcodes</h2>
      </div>

      {/* Lease selector for admins/landlords */}
      {['admin','landlord'].includes(user?.role) && (
        <div style={selector}>
          <label style={label}>Select Lease:</label>
          <select
            value={selectedLease}
            onChange={(e) => setSelectedLease(e.target.value)}
            style={dropdown}
          >
            <option value="">-- Choose Lease --</option>
            {leases.map(l => (
              <option key={l.lease_id} value={l.lease_id}>
                Lease #{l.lease_id} ‚Äì Property {l.property_id}
              </option>
            ))}
          </select>
        </div>
      )}

      {['admin','landlord'].includes(user?.role) && (
        <SmartPasscodeForm leases={leases} users={users} onCreated={handleCreated} />
      )}

      <div style={list}>
        <h3 style={listTitle}>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p style={empty}>No passcodes yet.</p>
        ) : (
          <div style={grid}>
            {passcodes.map(pc => (
              <SmartPasscode
                key={pc.passcode_id}
                passcode={pc}
                onRevoke={handleRevoke}
                onDelete={handleDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

/* üé® Styles */
const page = {
  maxWidth: '1000px',
  margin: '2rem auto',
  padding: '2rem',
  background: '#f9f9f9',
  borderRadius: '12px'
};
const header = {
  display: 'flex',
  alignItems: 'center',
  gap: '0.75rem',
  marginBottom: '1.5rem',
  borderBottom: '2px solid #eee',
  paddingBottom: '0.5rem'
};
const icon = { fontSize: '1.8rem' };
const title = { fontSize: '1.6rem', fontWeight: '600', margin: 0 };

const selector = { marginBottom: '1.5rem', display: 'flex', alignItems: 'center', gap: '0.5rem' };
const label = { fontWeight: '500', fontSize: '0.95rem' };
const dropdown = {
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};

const list = { marginTop: '2rem' };
const listTitle = { fontSize: '1.2rem', fontWeight: '600', marginBottom: '1rem' };
const empty = { color: '#666', fontStyle: 'italic' };

const grid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
  gap: '1.5rem'
};

export default PasscodesPage;
