import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/smart-passcodes', {
        lease_id: leaseId,
        user_id: userId,
        expires_at: expiresAt
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
    } catch (err) {
      console.error('‚ùå Create error:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={form}>
      <h3 style={formTitle}>Generate Smart Passcode</h3>

      {/* Lease selector */}
      <div style={field}>
        <label style={label}>Lease</label>
        <select
          value={leaseId}
          onChange={(e) => setLeaseId(e.target.value)}
          style={input}
        >
          <option value="">Select Lease</option>
          {leases.map(l => (
            <option key={l.lease_id} value={l.lease_id}>
              Lease #{l.lease_id} ‚Äì Property {l.property_id}
            </option>
          ))}
        </select>
      </div>

      {/* User selector */}
      <div style={field}>
        <label style={label}>User</label>
        <select
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          style={input}
        >
          <option value="">Select User</option>
          {users.map(u => (
            <option key={u.user_id} value={u.user_id}>
              {u.name || `User #${u.user_id}`}
            </option>
          ))}
        </select>
      </div>

      {/* Expiry input */}
      <div style={field}>
        <label style={label}>Expiry (optional)</label>
        <input
          type="datetime-local"
          value={expiresAt}
          onChange={(e) => setExpiresAt(e.target.value)}
          style={input}
        />
      </div>

      <button type="submit" style={button}>Generate Passcode</button>
    </form>
  );
};

/* üé® Styles */
const form = {
  background: '#fff',
  padding: '1.5rem',
  borderRadius: '12px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
  marginBottom: '2rem'
};
const formTitle = { fontSize: '1.2rem', fontWeight: '600', marginBottom: '1rem' };
const field = { marginBottom: '1rem' };
const label = { display: 'block', marginBottom: '0.5rem', fontWeight: '500' };
const input = {
  width: '100%',
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};
const button = {
  padding: '0.6rem 1.2rem',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600',
  cursor: 'pointer'
};

export default SmartPasscodeForm;
