import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import AdminPanel from './AdminPanel'; // ✅ Correct path if both are in pages/
import Profile from '../components/Profile';

export default function Dashboard() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/auth/profile')
      .then(res => setUser(res.data))
      .catch(err => {
        console.error('Profile fetch failed:', err);
        setUser({ error: true });
      });
  }, []);

  if (!user) return <p>Loading...</p>;
  if (user.error) return <p>Unauthorized. Please log in.</p>;

  return (
    <div>
      <h1>Welcome, {user.name}</h1>
      <p>Role: {user.role}</p>

      {user.role === 'admin' && (
        <>
          <AdminPanel />
          <div style={{ marginTop: '1rem' }}>
            <button onClick={() => navigate('/properties')}>Manage Properties</button>
            <button onClick={() => navigate('/tenants')}>View Tenants</button>
            <button onClick={() => navigate('/maintenance')}>Review Maintenance Requests</button>
            <button onClick={() => navigate('/passcodes')}>Access Smart Passcodes</button>
          </div>
        </>
      )}

      {(user.role === 'tenant' || user.role === 'landlord') && (
        <Profile user={user} />
      )}
    </div>
  );
}
