import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = useCallback(() => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    if (onLogout) onLogout();
    navigate("/login");
  }, [navigate, onLogout]);


  useEffect(() => {
  const stored = localStorage.getItem("user");

  if (!stored) {
    console.log("‚õî No user in localStorage yet ‚Äî waiting...");
    return; // ‚Üê prevent redirect until login stores user
  }

  const parsed = JSON.parse(stored);

  setUser(parsed);

  if (parsed.role !== "tenant") {
    navigate("/unauthorized");
  }

  api
    .get(`/user-reminders/${parsed.user_id}`)
    .then((res) => setReminders(res.data))
    .catch((err) => {
      const msg = err.response?.data?.message;
      if (msg === "Token expired") handleLogout();
      else console.error("‚ùå Error loading reminders:", msg || err.message);
    });
}, [navigate, handleLogout]);


  useEffect(() => {
    if (!user?.user_id) return;
    const loadLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get(`/leases/user/${user.user_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLease(res.data.lease || res.data);
      } catch (err) {
        const msg = err.response?.data?.message;
        if (msg === 'Token expired') {
          console.warn("‚ö†Ô∏è Token expired, logging out");
          handleLogout();
        } else {
          console.error("‚ùå Error loading lease:", msg || err.message);
        }
      }
    };
    loadLease();
  }, [user, handleLogout]);

  useEffect(() => {
    if (lease?.lease_id) {
      api.get(`/smart-passcodes/${lease.lease_id}`)
        .then((res) => setPasscodes(res.data))
        .catch((err) => {
          const msg = err.response?.data?.message;
          if (msg === 'Token expired') handleLogout();
          else console.error("‚ùå Error loading passcodes:", msg || err.message);
        });
    }
  }, [lease, handleLogout]);

  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post(
        '/payments/create-session',
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === 'Token expired') handleLogout();
      else console.error('‚ùå Payment error:', msg || err.message);
    }
  };

  return (
    <div style={styles.page}>
      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Tenant Portal</div>
        <div style={styles.navRight}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>Profile</button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>Dashboard</button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {/* SUMMARY BAR */}
      {lease && (
        <div style={styles.summaryBar}>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Next Rent Due</span>
            <span style={styles.summaryValue}>
              {parseFloat(lease.rent_amount || '0') > 0
                ? `$${parseFloat(lease.rent_amount).toFixed(2)}`
                : 'Not set'}
            </span>
          </div>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Lease Ends</span>
            <span style={styles.summaryValue}>
              {lease.end_date
                ? new Date(lease.end_date).toLocaleDateString()
                : '‚Äî'}
            </span>
          </div>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Payments Made</span>
            <span style={styles.summaryValue}>
              {lease.rent_amount && passcodes.length > 0
                ? `$${(passcodes.length * parseFloat(lease.rent_amount)).toFixed(2)}`
                : '‚Äî'}
            </span>
          </div>
        </div>
      )}

      {/* MAIN CONTENT */}
      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name || 'Guest'}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
        </div>

        <div style={styles.card}>
  <h3 style={styles.sectionTitle}>üìÑ Lease Information</h3>
  <TenantLeaseCard lease={lease} user={user} showTitle={false} />
</div>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Payment History</h3>
          <TenantPayments userId={user?.user_id} />
          <button style={styles.button} onClick={handlePaymentClick}>Make a Payment</button>
        </div>

        {lease && (
          <>
            <div style={styles.card}>
              <h3 style={styles.sectionTitle}>üì∑ Lease Photos</h3>
              <LeasePhotosForm 
              leaseId={lease.lease_id} 
              token={localStorage.getItem('token')} role={user?.role} />
            </div>

            <div style={styles.card}>
              <h3 style={styles.sectionTitle}>üìÑ Lease Documents</h3>
              <DocumentsForm leaseId={lease.lease_id} userId={user?.user_id} role={user?.role} />
            </div>
          </>
        )}

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Reminders</h3>
          {reminders.length === 0 ? (
            <p style={{ color: '#888' }}>No reminders yet üéâ</p>
          ) : (
            <ul style={styles.list}>
              {reminders.map((r) => (
                <li key={r.reminder_id}>
                  <strong>{r.type}:</strong> {r.message} ‚Äî {new Date(r.remind_at).toLocaleDateString()}
                </li>
              ))}
            </ul>
          )}
        </div>

        {lease && (
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>Smart Passcodes</h3>
            {passcodes.length === 0 ? (
              <p style={{ color: '#888' }}>No active passcodes</p>
            ) : (
              <ul style={styles.list}>
                {passcodes.map((pc) => (
                  <li key={pc.passcode_id}>
                    Code: {pc.passcode} ‚Äî Expires: {new Date(pc.expires_at).toLocaleString()}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
};


const styles = {
  page: {
    background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif',
    color: '#1f2937',
    display: 'flex',
    flexDirection: 'column'
  },

  /* NAVBAR */
  navbar: {
    background: '#1e3a8a',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    width: '100%'
  },
  navLeft: {
    fontWeight: 'bold',
    fontSize: '1.4rem'
  },
  navRight: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center'
  },
  navButton: {
    background: 'transparent',
    border: '1px solid #fff',
    color: '#fff',
    padding: '0.45rem 1rem',
    borderRadius: '8px',
    fontSize: '0.95rem',
    fontWeight: 500,
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },
  logoutButton: {
    background: '#dc2626',
    border: 'none',
    color: '#fff',
    padding: '0.55rem 1.1rem',
    fontWeight: 600,
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },

  /* SUMMARY BAR */
  summaryBar: {
    display: 'flex',
    justifyContent: 'center',
    gap: '2.2rem',
    background: '#ffffff',
    padding: '1.2rem 2rem',
    margin: '2rem auto',
    borderRadius: '16px',
    boxShadow: '0 4px 14px rgba(0,0,0,0.08)',
    maxWidth: '800px'
  },
  summaryItem: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    minWidth: '110px'
  },
  summaryLabel: {
    fontSize: '0.9rem',
    color: '#6b7280',
    marginBottom: '0.3rem'
  },
  summaryValue: {
    fontSize: '1.4rem',
    fontWeight: 700,
    color: '#1e3a8a'
  },

  /* MAIN CONTENT */
  container: {
    padding: '2.5rem 2rem',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
  },
  heading: {
    fontSize: '2rem',
    fontWeight: 700,
    marginBottom: '0.4rem',
    color: '#111'
  },
  role: {
    fontSize: '1rem',
    color: '#6b7280',
    marginBottom: '2rem'
  },

  /* CARD */
  card: {
    background: '#ffffff',
    borderRadius: '16px',
    padding: '2rem',
    width: '100%',
    maxWidth: '800px',
    marginBottom: '2rem',
    boxShadow: '0 4px 14px rgba(0,0,0,0.08)',
    transition: 'transform 0.2s ease, box-shadow 0.2s ease'
  },
  cardHover: {
    transform: 'translateY(-4px)',
    boxShadow: '0 6px 20px rgba(0,0,0,0.12)'
  },

  sectionTitle: {
    fontSize: '1.35rem',
    fontWeight: 600,
    marginBottom: '1rem',
    color: '#1f2937',
    borderBottom: '1px solid #e5e7eb',
    paddingBottom: '0.6rem'
  },

  /* BUTTON */
  button: {
    marginTop: '1.2rem',
    padding: '0.7rem 1.4rem',
    background: '#1e3a8a',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 600,
    cursor: 'pointer',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease'
  },

  /* LIST */
  list: {
    listStyle: 'none',
    padding: 0,
    margin: 0
  }
};


export default TenantDashboard;
