import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

export default function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/login', { email, password });
      const { token, user } = res.data;

      // ✅ Save token
      localStorage.setItem('token', token);

      // ✅ Flatten + normalize role before saving
      localStorage.setItem(
        'user',
        JSON.stringify({
          user_id: user.user_id,
          email: user.email,
          role: user.role.toLowerCase(), // normalize once here
          name: user.name,
        })
      );

      // ✅ Navigate based on role
      if (user.role.toLowerCase() === 'tenant') {
        navigate('/tenant');
      } else if (user.role.toLowerCase() === 'landlord') {
        navigate('/landlord');
      } else if (user.role.toLowerCase() === 'admin') {
        navigate('/admin-panel');
      } else {
        navigate('/unauthorized'); // fallback
      }
    } catch (err) {
      alert('Login failed. Check your credentials.');
    }
  };

  return (
    <form onSubmit={handleLogin} style={formStyle}>
      <h2 style={headingStyle}>Welcome Back</h2>

      <label style={labelStyle}>Email</label>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        style={inputStyle}
      />

      <label style={labelStyle}>Password</label>
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        autoComplete="current-password" // ✅ fixes Chrome warning
        style={inputStyle}
      />

      <div style={linkRowStyle}>
        <Link to="/forgot-password" style={linkStyle}>
          Forgot Password?
        </Link>
      </div>

      <button type="submit" style={buttonStyle}>Login</button>
    </form>
  );
}
