import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function LandlordProperties() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);

  useEffect(() => {
    if (!user || (user.role?.toLowerCase() !== 'landlord')) {
      navigate('/unauthorized');
      return;
    }

    Promise.all([
      api.get(`/properties/user?user_id=${user.id}`),
      api.get(`/leases/landlord-tenants/${user.id}`),
      api.get(`/payments/landlord/${user.id}`),
      api.get(`/maintenance/landlord/${user.id}`)
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord properties:", err.response?.data || err.message)
      );
  }, [user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div style={pageStyle}>
      <nav style={navStyle}>
        <div style={navLeft}>üè† Landlord Portal</div>
        <div style={navRight}>
          <button style={navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={containerStyle}>
        <h2 style={headingStyle}>My Properties</h2>

        {properties.length === 0 ? (
          <p>No properties found.</p>
        ) : (
          <div style={cardGrid}>
            {properties.map(p => (
              <div key={p.property_id} style={card}>
                <h3 style={cardTitle}>{p.address} ‚Äî {p.city}, {p.state}</h3>
                <p>Status: {p.status}</p>
                <p>Rent Amount: ${p.rent_amount}</p>

                <h4>üë• Tenants</h4>
                <ul>
                  {tenants.filter(t => t.property_id === p.property_id).map(t => (
                    <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                  ))}
                </ul>

                <h4>üí≥ Payments</h4>
                <ul>
                  {payments.filter(pay => pay.property_id === p.property_id).map(pay => (
                    <li key={pay.payment_id}>
                      {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
                    </li>
                  ))}
                </ul>

                <h4>üõ†Ô∏è Maintenance</h4>
                <ul>
                  {maintenance.filter(m => m.property_id === p.property_id).map(m => (
                    <li key={m.request_id}>
                      {m.type}: {m.description} ‚Äî {m.status}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

/* Styles */
const pageStyle = { background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' };
const navStyle = { background: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem 2rem', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', position: 'sticky', top: 0, zIndex: 1000, width: '100%' };
const navLeft = { fontWeight: 'bold', fontSize: '1.5rem' };
const navRight = { display: 'flex', gap: '1rem' };
const navButton = { background: 'transparent', border: '1px solid #fff', color: '#fff', fontSize: '1rem', fontWeight: '500', cursor: 'pointer', padding: '0.5rem 1rem', borderRadius: '6px' };
const logoutButton = { background: '#dc2626', border: 'none', color: '#fff', padding: '0.5rem 1rem', borderRadius: '6px', fontWeight: '500', cursor: 'pointer' };
const containerStyle = { padding: '2rem', width: '100%', maxWidth: '1200px', margin: '0 auto' };
const headingStyle = { fontSize: '2rem', color: '#111', marginBottom: '1.5rem' };
const cardGrid = { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '1.5rem' };
const card = { backgroundColor: '#fff', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.08)', padding: '1.5rem', transition: 'transform 0.2s ease, box-shadow 0.2s ease' };
const cardTitle = { fontSize: '1.25rem', fontWeight: '600', marginBottom: '0.5rem', color: '#111' };
