import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SmartPasscode from '../components/SmartPasscode';
import SmartPasscodeForm from '../components/SmartPasscodeForm';

const PasscodesPage = () => {
  const [leases, setLeases] = useState([]);
  const [users, setUsers] = useState([]);
  const [passcodes, setPasscodes] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = token ? { Authorization: `Bearer ${token}` } : undefined;

        const leasesRes = await axios.get('/api/leases', { headers });
        const usersRes = await axios.get('/api/users', { headers });

        setLeases(leasesRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        console.error('❌ Fetch error:', err);
        setError('Failed to load leases or users.');
      }
    };

    fetchData();
  }, []);

  const handleRevoke = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/passcodes/${id}/revoke`, {}, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.map(p => p.passcode_id === id ? { ...p, is_active: false } : p));
    } catch (err) {
      console.error('❌ Revoke error:', err);
      alert('Failed to revoke passcode.');
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('❌ Delete error:', err);
      alert('Failed to delete passcode.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Smart Passcodes</h2>

      <SmartPasscodeForm
        leases={leases}
        users={users}
        onCreated={(newPasscode) => {
          setPasscodes([...passcodes, newPasscode]);
          setError('');
          setSuccess('✅ Passcode generated successfully!');
        }}
      />

      {error && <div style={errorMsg}>{error}</div>}
      {success && <div style={successMsg}>{success}</div>}

      <div style={{ marginTop: '2rem' }}>
        <h3>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p>No active passcodes yet. Generate one above to get started.</p>
        ) : (
          <div style={cardGrid}>
            {passcodes.map((p) => (
              <SmartPasscode
                key={p.passcode_id}
                passcode={p}
                onRevoke={handleRevoke}
                onDelete={handleDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const errorMsg = { color: '#e74c3c', fontWeight: '500', marginTop: '0.5rem' };
const successMsg = { color: '#2ecc71', fontWeight: '500', marginTop: '0.5rem' };
const cardGrid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
  gap: '1rem',
  marginTop: '1rem'
};

export default PasscodesPage;
