import React, { useState } from 'react';
import axios from 'axios';

axios.defaults.baseURL = 'http://localhost:5000';

const LeaseForm = ({ onCreate, user }) => {
  const [formData, setFormData] = useState({
    property_id: '',
    tenant_id: '',
    start_date: '',
    end_date: '',
    rent_amount: '',
    status: 'active'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/leases', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreate(res.data);
      setFormData({
        property_id: '',
        tenant_id: '',
        start_date: '',
        end_date: '',
        rent_amount: '',
        status: 'active'
      });
    } catch (err) {
      console.error('Create lease error:', err);
    }
  };

  // ✅ Allow both admin and landlord
  const canCreate = user && ['admin', 'landlord'].includes(user.role);
  if (!canCreate) return null;

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h3>Add New Lease</h3>
      <input name="property_id" placeholder="Property ID" value={formData.property_id} onChange={handleChange} required />
      <input name="tenant_id" placeholder="Tenant ID" value={formData.tenant_id} onChange={handleChange} required />
      <input name="start_date" type="date" value={formData.start_date} onChange={handleChange} required />
      <input name="end_date" type="date" value={formData.end_date} onChange={handleChange} required />
      <input name="rent_amount" type="number" placeholder="Rent Amount" value={formData.rent_amount} onChange={handleChange} required />
      <select name="status" value={formData.status} onChange={handleChange}>
        <option value="active">Active</option>
        <option value="terminated">Terminated</option>
        <option value="pending">Pending</option>
      </select>
      <button type="submit">Create Lease</button>
    </form>
  );
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem',
  marginBottom: '2rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

export default LeaseForm;
