import React, { useState } from 'react';
import axios from 'axios';
import SmartPasscode from '../components/SmartPasscode';
import SmartPasscodeForm from '../components/SmartPasscodeForm';

const PasscodesPage = ({ leases = [], users = [] }) => {
  const [passcodes, setPasscodes] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Revoke handler
  const handleRevoke = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/smart-passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('‚ùå Revoke error:', err);
      alert('Failed to revoke passcode.');
    }
  };

  // Delete handler
  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/smart-passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
      alert('Failed to delete passcode.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Smart Passcodes</h2>

      {/* Form */}
      <SmartPasscodeForm
        leases={leases}
        users={users}
        onCreated={(newPasscode) => {
          setPasscodes([...passcodes, newPasscode]);
          setError('');
          setSuccess('‚úÖ Passcode generated successfully!');
        }}
      />

      {/* Feedback */}
      {error && <div style={errorMsg}>{error}</div>}
      {success && <div style={successMsg}>{success}</div>}

      {/* Active Passcodes */}
      <div style={{ marginTop: '2rem' }}>
        <h3>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p>No active passcodes yet. Generate one above to get started.</p>
        ) : (
          <div style={cardGrid}>
            {passcodes.map((p) => (
              <SmartPasscode
                key={p.passcode_id}
                passcode={p}
                onRevoke={handleRevoke}
                onDelete={handleDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

/* üé® Styles */
const errorMsg = { color: '#e74c3c', fontWeight: '500', marginTop: '0.5rem' };
const successMsg = { color: '#2ecc71', fontWeight: '500', marginTop: '0.5rem' };
const cardGrid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
  gap: '1rem',
  marginTop: '1rem'
};

export default PasscodesPage;
