import React, { useEffect, useState } from 'react';
import api from '../services/api';

const PaymentsPage = ({ user }) => {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('âŒ No token found, user not authenticated');
          setLoading(false);
          return;
        }

const endpoint = '/payments/user'; // always use this
        const res = await api.get(endpoint, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPayments(res.data);
      } catch (err) {
        console.error('âŒ Failed to fetch payments:', err.response?.data || err.message);
        setError(err.response?.data?.error || 'Failed to fetch payments.');
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, [user]);

  const totalPaid = payments.reduce((sum, p) => sum + p.amount, 0);
  const lastPaymentDate = payments.length
    ? new Date(payments[0].paid_date).toLocaleDateString()
    : 'N/A';

  return (
    <div style={page}>
      <h2 style={heading}>ðŸ’³ Payments</h2>

      {/* Summary bar for admin */}
      {user?.role === 'admin' && (
        <div style={summaryBar}>
          <span><strong>Total Paid:</strong> ${totalPaid.toFixed(2)}</span>
          <span><strong>Last Payment:</strong> {lastPaymentDate}</span>
          <span><strong>Records:</strong> {payments.length}</span>
        </div>
      )}

      {loading ? (
        <p style={info}>Loading payments...</p>
      ) : error ? (
        <p style={errorMsg}>{error}</p>
      ) : payments.length === 0 ? (
        <p style={info}>No payments found.</p>
      ) : (
        <div style={list}>
          {payments.map((p, index) => (
            <div key={p.payment_id || index} style={card}>
              <p style={amount}>
                {new Intl.NumberFormat('en-US', {
                  style: 'currency',
                  currency: 'USD'
                }).format(p.amount)}
              </p>
              <p style={meta}>Date: {new Date(p.paid_date).toLocaleDateString()}</p>
              {user?.role === 'admin' && (
                <>
                  <p style={meta}>Tenant: {p.tenant_name || p.user_name || 'N/A'}</p>
                  <p style={meta}>Property ID: {p.property_id || 'N/A'}</p>
                </>
              )}
              <p style={meta}>Payment ID: {p.payment_id}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

/* ðŸŽ¨ Styles */
const page = {
  padding: '2rem',
  maxWidth: '960px',
  margin: '0 auto',
  fontFamily: 'Segoe UI, sans-serif'
};

const heading = {
  fontSize: '1.75rem',
  fontWeight: '600',
  marginBottom: '1.5rem',
  color: '#111'
};

const summaryBar = {
  display: 'flex',
  justifyContent: 'space-between',
  backgroundColor: '#f3f4f6',
  padding: '0.75rem 1rem',
  borderRadius: '8px',
  marginBottom: '1.5rem',
  fontSize: '0.95rem',
  fontWeight: '500',
  color: '#333'
};

const info = {
  fontSize: '1rem',
  color: '#555'
};

const errorMsg = {
  fontSize: '1rem',
  color: '#dc2626',
  fontWeight: '500'
};

const list = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
  gap: '1rem',
  marginBottom: '2rem'
};

const card = {
  backgroundColor: '#fff',
  borderRadius: '10px',
  boxShadow: '0 2px 6px rgba(0,0,0,0.08)',
  padding: '1rem',
  display: 'flex',
  flexDirection: 'column',
  gap: '0.4rem'
};

const amount = {
  fontSize: '1.2rem',
  fontWeight: '600',
  color: '#111'
};

const meta = {
  fontSize: '0.9rem',
  color: '#555'
};

export default PaymentsPage;
