import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PropertyList = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    async function fetchProperties() {
      try {
        const res = await axios.get('/api/properties', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setProperties(res.data);
      } catch (err) {
        console.error('Property fetch error:', err);
      }
    }
    fetchProperties();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/properties/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setProperties(prev => prev.filter(p => p.property_id !== id));
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  return (
    <div style={listStyle}>
      <h3>All Properties</h3>
      {properties.length === 0 ? (
        <p>No properties found.</p>
      ) : (
        properties.map(prop => (
          <div key={prop.property_id} style={cardStyle}>
            <p><strong>Address:</strong> {prop.address}, {prop.city}, {prop.state} {prop.zip}</p>
            <p><strong>Rent:</strong> ${prop.rent_amount}</p>
            <p><strong>Status:</strong> {prop.status}</p>
            <p><strong>Owner:</strong> User #{prop.user_id}</p>
            <button onClick={() => handleDelete(prop.property_id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

const listStyle = {
  padding: '2rem',
  fontFamily: 'Segoe UI, sans-serif',
  backgroundColor: '#fff',
  borderRadius: '10px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
};

const cardStyle = {
  marginBottom: '1rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

export default PropertyList;
