import React, { useEffect, useState } from "react";
import api from "../services/api";

const TenantLeaseCard = ({ user, onLeaseLoaded }) => {
  const [lease, setLease] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadLease = async () => {
      try {
        const res = await api.get(`/leases/user/${user.id}`);
        setLease(res.data);
        onLeaseLoaded(res.data); // ← IMPORTANT: sends lease to dashboard
      } catch (err) {
        console.error("❌ Error loading lease:", err.response?.data || err);
      } finally {
        setLoading(false);
      }
    };

    if (user?.id) {
      loadLease();
    }
  }, [user?.id]);

  if (loading) return <p>Loading lease...</p>;
  if (!lease) return <p>No lease found.</p>;

  return (
    <div>
      <h4>Your Lease</h4>

      <p><strong>Lease ID:</strong> {lease.lease_id}</p>
      <p><strong>Property ID:</strong> {lease.property_id}</p>
      <p><strong>Start:</strong> {new Date(lease.start_date).toLocaleDateString()}</p>
      <p><strong>End:</strong> {new Date(lease.end_date).toLocaleDateString()}</p>

      {lease.lease_file_url && (
        <p>
          <a
            href={`http://localhost:5000/uploads/${lease.lease_file_url}`}
            target="_blank"
            rel="noreferrer"
          >
            View Lease PDF
          </a>
        </p>
      )}
    </div>
  );
};

export default TenantLeaseCard;
