import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LeaseForm from './LeaseForm';
import LeaseList from './LeaseList';

axios.defaults.baseURL = 'http://localhost:5000';

const AdminLeases = () => {
  const [leases, setLeases] = useState([]);
  const [selectedLease, setSelectedLease] = useState(null);

  const fetchLeases = async () => {
    try {
      const res = await axios.get('/api/leases', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setLeases(res.data);
    } catch (err) {
      console.error('Fetch leases error:', err);
    }
  };

  const handleCreate = async (newLease) => {
    setLeases(prev => [...prev, newLease]);
  };

  const handleEdit = (lease) => {
    setSelectedLease(lease);
    // Optional: wire up edit form later
  };

  const handleDelete = async (leaseId) => {
    try {
      await axios.delete(`/api/leases/${leaseId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setLeases(prev => prev.filter(l => l.lease_id !== leaseId));
    } catch (err) {
      console.error('Delete lease error:', err);
    }
  };

  useEffect(() => {
    fetchLeases();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <LeaseForm onCreate={handleCreate} />
      <LeaseList leases={leases} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
};

export default AdminLeases;
