import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ user, onLeaseLoaded }) => {
  const [lease, setLease] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user?.id) return;

    const fetchLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const url = `/api/leases/user/${user.id}`;

        const res = await axios.get(url, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const userLease = res.data || null;
        setLease(userLease);

        // Pass lease back to dashboard
        if (typeof onLeaseLoaded === 'function') {
          onLeaseLoaded(userLease);
        }
      } catch (err) {
        console.error('❌ Lease fetch error:', err.response?.data || err.message);
        setLease(null);

        if (typeof onLeaseLoaded === 'function') {
          onLeaseLoaded(null);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchLease();
  }, [user?.id, onLeaseLoaded]); // ✅ include onLeaseLoaded in deps

  if (loading) return <p>Loading lease...</p>;

  return (
    <div style={cardStyle}>
      {lease ? (
        <>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>

          <p><strong>Property ID:</strong> {lease.property_id || 'N/A'}</p>

          <p><strong>Rent:</strong> {lease.rent_amount ? `$${lease.rent_amount}` : 'Not provided'}</p>

          <p><strong>Start:</strong> {lease.start_date ? new Date(lease.start_date).toLocaleDateString() : 'N/A'}</p>

          <p><strong>End:</strong> {lease.end_date ? new Date(lease.end_date).toLocaleDateString() : 'N/A'}</p>

          <p><strong>Status:</strong> {lease.renewal_requested ? 'Renewal requested' : 'Active'}</p>

          {lease.lease_file_url && (
            <p>
              <strong>Lease File:</strong>{' '}
              <a
                href={`http://localhost:5000/uploads/${lease.lease_file_url}`}
                target="_blank"
                rel="noreferrer"
              >
                View File
              </a>
            </p>
          )}
        </>
      ) : (
        <p>No lease found.</p>
      )}
    </div>
  );
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff',
  marginBottom: '2rem',
};

export default TenantLeaseCard;
