import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SmartPasscodeForm from '../components/SmartPasscodeForm';
import SmartPasscode from '../components/SmartPasscode';

const PasscodesPage = () => {
  const [leases, setLeases] = useState([]);
  const [users, setUsers] = useState([]);
  const [passcodes, setPasscodes] = useState([]);
  const [selectedLease, setSelectedLease] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = token ? { Authorization: `Bearer ${token}` } : undefined;

        const leasesRes = await axios.get('/api/leases', { headers });
        const usersRes = await axios.get('/api/passcodes/users-with-leases', { headers });

        setLeases(leasesRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        console.error('‚ùå Fetch error:', err);
        setError('Failed to load leases or users.');
      }
    };

    fetchData();
  }, []);

  // üîë Fetch passcodes when lease changes
  useEffect(() => {
    const fetchPasscodes = async () => {
      if (!selectedLease) return;
      try {
        const token = localStorage.getItem('token');
        const headers = token ? { Authorization: `Bearer ${token}` } : undefined;
        const res = await axios.get(`/api/passcodes/${selectedLease}`, { headers });
        setPasscodes(res.data);
      } catch (err) {
        console.error('‚ùå Passcode fetch error:', err);
        setError('Failed to load passcodes.');
      }
    };

    fetchPasscodes();
  }, [selectedLease]);

  const handleRevoke = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/passcodes/${id}/revoke`, {}, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.map(p => p.passcode_id === id ? { ...p, is_active: false } : p));
    } catch (err) {
      console.error('‚ùå Revoke error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Smart Passcodes</h2>

      {/* Lease selector for fetching passcodes */}
      <label>Select Lease to View Passcodes</label>
      <select
        value={selectedLease}
        onChange={(e) => setSelectedLease(e.target.value)}
      >
        <option value="" disabled>Select Lease</option>
        {leases.map((l) => (
          <option key={l.lease_id} value={String(l.lease_id)}>
            Lease #{l.lease_id} ‚Äì Property {l.property_id}
          </option>
        ))}
      </select>

      <SmartPasscodeForm
        leases={leases}
        users={users}
        onCreated={(newPasscode) => setPasscodes([...passcodes, newPasscode])}
      />

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <div style={{ marginTop: '2rem' }}>
        <h3>Passcodes for Lease {selectedLease || '(none selected)'}</h3>
        {passcodes.length === 0 ? (
          <p>No passcodes yet.</p>
        ) : (
          <div style={{ display: 'grid', gap: '1rem' }}>
            {passcodes.map((p) => (
              <SmartPasscode
                key={p.passcode_id}
                passcode={p}
                onRevoke={handleRevoke}
                onDelete={handleDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PasscodesPage;
