import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantsPage = () => {
  const [tenants, setTenants] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    async function fetchTenants() {
      try {
        const res = await axios.get('/api/users?role=tenant', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setTenants(res.data);
      } catch (err) {
        console.error('Fetch tenants error:', err);
      } finally {
        setLoading(false);
      }
    }
    fetchTenants();
  }, []);

  if (!user || user.role !== 'admin') {
    return <p style={unauthorizedStyle}>Unauthorized access</p>;
  }

  if (loading) {
    return <p style={loadingStyle}>Loading tenants...</p>;
  }

  return (
    <div style={containerStyle}>
      <h2>Registered Tenants</h2>
      {tenants.length === 0 ? (
        <p>No tenants found.</p>
      ) : (
        <ul style={listStyle}>
          {tenants.map(t => (
            <li key={t.id} style={cardStyle}>
              <p><strong>Name:</strong> {t.name}</p>
              <p><strong>Email:</strong> {t.email}</p>
              <p><strong>Phone:</strong> {t.phone || 'N/A'}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const containerStyle = {
  padding: '2rem',
  fontFamily: 'Segoe UI, sans-serif',
  backgroundColor: '#f9f9f9',
  minHeight: '100vh'
};

const listStyle = {
  listStyleType: 'none',
  padding: 0,
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem'
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff'
};

const loadingStyle = {
  textAlign: 'center',
  fontSize: '1.2rem',
  marginTop: '3rem',
  color: '#555'
};

const unauthorizedStyle = {
  textAlign: 'center',
  fontSize: '1.2rem',
  marginTop: '3rem',
  color: '#c00'
};

export default TenantsPage;
