import React from 'react';
import { useNavigate } from 'react-router-dom';

const LandlordDashboard = ({ user, onLogout }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord/properties')}>Properties</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/tenants')}>Tenants</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/payments')}>Payments</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/maintenance')}>Maintenance</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>
        <p style={styles.info}>Use the navigation above to manage your properties, tenants, payments, and maintenance requests.</p>
      </div>
    </div>
  );
};

const styles = {
  page: { background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' },
  navbar: { background: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem 2rem', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', position: 'sticky', top: 0, zIndex: 1000 },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: { background: 'transparent', border: '1px solid #fff', color: '#fff', cursor: 'pointer', padding: '0.4rem 0.8rem', borderRadius: '6px', transition: 'background 0.2s ease' },
  logoutButton: { background: '#dc3545', border: 'none', color: '#fff', padding: '0.5rem 1rem', borderRadius: '6px', cursor: 'pointer', transition: 'background 0.2s ease' },
  container: { padding: '2rem', maxWidth: '1200px', margin: '0 auto', textAlign: 'center' },
  heading: { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' },
  role: { fontSize: '1rem', marginBottom: '1rem', color: '#666' },
  info: { fontSize: '1.1rem', color: '#333' }
};

export default LandlordDashboard;
