import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const lease_id = Number(leaseId);
    const user_id = Number(userId);
    if (!lease_id || !user_id) {
      setError('Please select valid Lease and User.');
      return;
    }

    try {
      const res = await axios.post('/api/smart-passcodes', {
        lease_id,
        user_id,
        expires_at: expiresAt || null
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
      setError('');
    } catch {
      setError('Failed to generate passcode.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={form}>
      <h3 style={title}>Generate Smart Passcode</h3>

      <div style={row}>
        <select value={leaseId} onChange={(e) => setLeaseId(e.target.value)} style={select}>
          <option value="" disabled>Select Lease</option>
          {leases
            .filter(l => l.lease_id)
            .sort((a, b) => a.lease_id - b.lease_id)
            .map((l) => (
              <option key={`lease-${l.lease_id}`} value={String(l.lease_id)}>
                Lease #{l.lease_id} â€“ Property {l.property_id}
              </option>
            ))}
        </select>

        <select value={userId} onChange={(e) => setUserId(e.target.value)} style={select}>
          <option value="" disabled>Select User</option>
          {users
            .filter(u => u.user_id)
            .sort((a, b) => a.user_id - b.user_id)
            .map((u) => (
              <option key={`user-${u.user_id}`} value={String(u.user_id)}>
                {u.name || `User #${u.user_id}`}
              </option>
            ))}
        </select>
      </div>

      <input
        type="datetime-local"
        value={expiresAt}
        onChange={(e) => setExpiresAt(e.target.value)}
        style={input}
        placeholder="Expiry (optional)"
      />

      <button type="submit" style={button}>Generate Passcode</button>

      {error && <p style={errorMsg}>{error}</p>}
    </form>
  );
};

const form = {
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  padding: '1rem',
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
};
const title = { fontSize: '1.1rem', fontWeight: '600', marginBottom: '0.5rem' };
const row = { display: 'flex', gap: '1rem' };
const select = {
  flex: 1,
  padding: '0.5rem',
  borderRadius: '6px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};
const input = {
  padding: '0.5rem',
  borderRadius: '6px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};
const button = {
  padding: '0.6rem 1.2rem',
  backgroundColor: '#3498db',
  color: '#fff',
  border: 'none',
  borderRadius: '6px',
  fontWeight: '600',
  cursor: 'pointer',
  alignSelf: 'flex-start'
};
const errorMsg = { color: '#e74c3c', fontWeight: '500' };

export default SmartPasscodeForm;
