import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [users, setUsers] = useState([]);
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [submitting, setSubmitting] = useState(false);

  // üîÑ Fetch users whenever a lease is selected
  useEffect(() => {
    const fetchUsersForLease = async () => {
      if (!leaseId) {
        setUsers([]);
        return;
      }
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`/api/leases/${leaseId}/users`, {
          headers: token ? { Authorization: `Bearer ${token}` } : undefined
        });
        setUsers(res.data);
      } catch (err) {
        console.error('‚ùå Fetch users error:', err);
        setError('Failed to load users for this lease.');
      }
    };
    fetchUsersForLease();
  }, [leaseId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const lease_id = Number(leaseId);
    const user_id = Number(userId);
    const expires_at =
      expiresAt && !isNaN(new Date(expiresAt))
        ? new Date(expiresAt).toISOString()
        : null;

    if (!lease_id || !user_id || isNaN(lease_id) || isNaN(user_id)) {
      setError('Please select valid Lease and User.');
      return;
    }

    setSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        '/api/smart-passcodes',
        { lease_id, user_id, expires_at },
        token ? { headers: { Authorization: `Bearer ${token}` } } : undefined
      );

      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
      setSuccess('‚úÖ Passcode generated successfully!');
    } catch (err) {
      console.error('‚ùå Create error:', err);
      setError('Failed to generate passcode. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={form}>
      <h3 style={formTitle}>Generate Smart Passcode</h3>

      <div style={row}>
        <div style={field}>
          <label style={label}>Lease</label>
          <select
            value={leaseId}
            onChange={(e) => {
              setLeaseId(e.target.value);
              setUserId('');
              setError('');
              setSuccess('');
            }}
            style={select}
          >
            <option value="" disabled>Select Lease</option>
            {(leases || [])
              .filter(l => l.lease_id)
              .sort((a, b) => a.lease_id - b.lease_id)
              .map((l) => (
                <option key={`lease-${l.lease_id}`} value={String(l.lease_id)}>
                  Lease #{l.lease_id} ‚Äì Property {l.property_id}
                </option>
              ))}
          </select>
        </div>

        <div style={field}>
          <label style={label}>User</label>
          <select
            value={userId}
            onChange={(e) => {
              setUserId(e.target.value);
              setError('');
              setSuccess('');
            }}
            style={select}
            disabled={!leaseId}
          >
            <option value="" disabled>
              {leaseId ? 'Select User' : 'Choose lease first'}
            </option>
            {(users || [])
              .sort((a, b) => a.user_id - b.user_id)
              .map((u) => (
                <option key={`user-${u.user_id}`} value={String(u.user_id)}>
                  {u.name || `User #${u.user_id}`}
                </option>
              ))}
          </select>
        </div>
      </div>

      <div style={field}>
        <label style={label}>Expiry (optional)</label>
        <input
          type="datetime-local"
          value={expiresAt}
          onChange={(e) => setExpiresAt(e.target.value)}
          style={input}
        />
      </div>

      <button
        type="submit"
        style={{
          ...button,
          opacity: submitting ? 0.7 : 1,
          cursor: submitting ? 'not-allowed' : 'pointer'
        }}
        disabled={submitting}
      >
        {submitting ? 'Generating‚Ä¶' : 'Generate Passcode'}
      </button>

      {error && <div style={errorMsg}>{error}</div>}
      {success && <div style={successMsg}>{success}</div>}
    </form>
  );
};

/* üé® Styles */
const form = {
  background: '#fff',
  padding: '1.5rem',
  borderRadius: '12px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
  marginBottom: '2rem',
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  maxWidth: 600
};
const formTitle = { fontSize: '1.2rem', fontWeight: '600' };
const row = { display: 'flex', gap: '1rem', flexWrap: 'wrap' };
const field = { flex: 1, display: 'flex', flexDirection: 'column' };
const label = { marginBottom: '0.4rem', fontWeight: '500', fontSize: '0.9rem' };
const select = {
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem',
  backgroundColor: '#fff',
  cursor: 'pointer'
};
const input = {
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};
const button = {
  alignSelf: 'flex-start',
  padding: '0.6rem 1.2rem',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600'
};
const errorMsg = { color: '#e74c3c', fontWeight: '500' };
const successMsg = { color: '#2ecc71', fontWeight: '500' };

export default SmartPasscodeForm;
