// src/components/TenantPayments.jsx

import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function TenantPayments({ userId }) {
  const [payments, setPayments] = useState(null);

  useEffect(() => {
api.get(`/payment-history/user`)
      .then(res => setPayments(res.data))
      .catch(() => setPayments([]));
  }, [userId]);

  if (!payments) return <p>Loading payment history...</p>;
  if (payments.length === 0) return <p>No payments found.</p>;

  return (
    <div>
      <ul>
        {payments.map((payment) => (
         <li key={payment.payment_id}>
  <strong>Date:</strong> {new Date(payment.paid_date).toLocaleDateString()} |
  <strong> Amount:</strong> ${payment.amount} |
  <strong> Status:</strong> {payment.status} |
  <strong> Late Fee:</strong> {payment.late_fee_applied ? 'Yes' : 'No'} |
  <strong> Note:</strong> {payment.note}
</li>
        ))}
      </ul>
    </div>
  );
}
