/* ---------- TenantDashboard UI Update (Refined) ---------- */
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  const location = useLocation();

  const accent = '#1e3a8a';

  const handleLogout = useCallback(() => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    if (onLogout) onLogout();
    navigate("/login");
  }, [navigate, onLogout]);

  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (!stored) return;
    const parsed = JSON.parse(stored);
    setUser(parsed);
    if (parsed.role !== "tenant") navigate("/unauthorized");

    const token = localStorage.getItem('token');

    // Parallel fetching for better performance
    Promise.all([
      api.get(`/user-reminders/${parsed.user_id}`).then(res => setReminders(res.data)),
      api.get(`/leases/user/${parsed.user_id}`, { headers: { Authorization: `Bearer ${token}` } }).then(res => setLease(res.data.lease || res.data)),
      api.get(`/payments/user`, { headers: { Authorization: `Bearer ${token}` } }).then(res => setPayments(res.data || []))
    ]).catch(err => {
      const msg = err.response?.data?.message;
      if (msg === 'Token expired') handleLogout();
      else setError(msg || 'Error loading dashboard');
    }).finally(() => setLoading(false));

  }, [navigate, handleLogout]);

  useEffect(() => {
    if (lease?.lease_id) {
      const token = localStorage.getItem('token');
      api.get(`/smart-passcodes/${lease.lease_id}`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => setPasscodes(res.data))
        .catch(err => {
          const msg = err.response?.data?.message;
          if (msg === 'Token expired') handleLogout();
          else console.error("‚ùå Error loading passcodes:", msg || err.message);
        });
    }
  }, [lease, handleLogout]);

  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post(
        '/payments/create-session',
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === 'Token expired') handleLogout();
      else alert('Payment error: ' + (msg || err.message));
    }
  };

  // Calculate total payments
  const totalPayments = payments.reduce((sum, p) => sum + (p.amount || 0), 0);

  return (
    <div style={styles.page}>
      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.brand}>
          <div style={styles.brandIcon}>üè†</div>
          <div style={styles.brandText}>Tenant Portal</div>
        </div>
        <div style={styles.rightControls}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>Profile</button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>Dashboard</button>
          )}
          <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
        </div>
      </nav>

      {/* ERROR MESSAGE */}
      {error && <div style={styles.error}>{error}</div>}

      {/* SUMMARY */}
      <section style={styles.summaryWrap}>
        {loading ? (
          <div>Loading summary...</div>
        ) : (
          <div style={styles.summaryGrid}>
            <CardGlass label="Next Rent Due" icon="üí∞" value={lease ? `$${lease.rent_amount}` : '‚Äî'} accent={accent} />
            <CardGlass label="Lease Ends" icon="üìÖ" value={lease?.end_date ? new Date(lease.end_date).toLocaleDateString() : '‚Äî'} accent={accent} />
            <CardGlass label="Payments Made" icon="üí≥" value={`$${totalPayments}`} accent={accent} />
          </div>
        )}
      </section>

      {/* MAIN GRID */}
      <main style={styles.mainContainer}>
        <header style={styles.header}>
          <div>
            <h1 style={styles.title}>Welcome, <span style={{ color: accent }}>{user?.name ?? 'Guest'}</span></h1>
            <div style={styles.subTitle}>Role: <span>{user?.role}</span></div>
          </div>
          <button onClick={handlePaymentClick} style={styles.actionBtn} disabled={!lease}>Make Payment</button>
        </header>

        {loading ? (
          <div>Loading dashboard...</div>
        ) : (
          <section style={styles.grid}>
            {/* LEFT COLUMN */}
            <div>
              <GlassCard title="üë§ Tenant Profile">
                <p style={styles.meta}><strong>Name:</strong> {user?.name ?? '‚Äî'}</p>
                <p style={styles.meta}><strong>Email:</strong> {user?.email ?? '‚Äî'}</p>
              </GlassCard>

              <GlassCard title="üìÑ Lease Information">
                <TenantLeaseCard lease={lease} user={user} showTitle={false} />
              </GlassCard>

              <GlassCard title="üñº Lease Photos">
                <LeasePhotosForm leaseId={lease?.lease_id} token={localStorage.getItem('token')} role={user?.role} showTitle={false} />
              </GlassCard>

              <GlassCard title="üìÅ Lease Documents">
                <DocumentsForm leaseId={lease?.lease_id} userId={user?.user_id} role={user?.role} showTitle={false} />
              </GlassCard>
            </div>

            {/* RIGHT COLUMN */}
            <div>
              <GlassCard title="üí≥ Payment History">
                <TenantPayments userId={user?.user_id} />
              </GlassCard>

              <GlassCard title="üîî Reminders">
                {reminders.length === 0 ? <p style={styles.empty}>No reminders yet üéâ</p> : (
                  <ul style={styles.list}>{reminders.map(r => (
                    <li key={r.reminder_id} style={styles.listItem}>
                      <strong>{r.type}:</strong> {r.message} ‚Äî {new Date(r.remind_at).toLocaleDateString()}
                    </li>
                  ))}</ul>
                )}
              </GlassCard>

              <GlassCard title="üîê Smart Passcodes">
                {passcodes.length === 0 ? <p style={styles.empty}>No active passcodes</p> : (
                  <ul style={styles.list}>{passcodes.map(pc => (
                    <li key={pc.passcode_id} style={styles.listItem}>
                      <strong>Code:</strong> {pc.passcode} ‚Äî Expires: {new Date(pc.expires_at).toLocaleString()}
                    </li>
                  ))}</ul>
                )}
              </GlassCard>

              <div style={{ height: 12 }} />
              <div style={styles.footerNote}>
                <strong>Support:</strong> Contact your property manager for questions or maintenance requests.
              </div>
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

/* ---------- GlassCard Component ---------- */
const GlassCard = ({ title, children }) => (
  <div style={styles.glassCard}>
    <div style={styles.cardTitle}>{title}</div>
    <div style={styles.cardBody}>{children}</div>
  </div>
);

/* ---------- Summary Card Component ---------- */
function CardGlass({ label, icon, value, accent }) {
  return (
    <div style={{ ...styles.glassCard, textAlign: 'center', minWidth: 200, padding: '20px 24px' }}>
      <div style={{ fontSize: 26 }}>{icon}</div>
      <div style={{ color: '#1f2937', fontWeight: 600, marginTop: 8 }}>{label}</div>
      <div style={{ fontSize: 20, marginTop: 6, fontWeight: 700, color: accent }}>{value}</div>
    </div>
  );
}



const styles = {
  page: {
    background: 'linear-gradient(to bottom, #e2e8f0, #f8fafc)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif',
    color: '#1f2937',
    display: 'flex',
    flexDirection: 'column'
  },
  navbar: {
    background: '#1e3a8a',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    position: 'sticky',
    top: 0,
    zIndex: 1000
  },
  brand: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.6rem',
    fontSize: '1.4rem',
    fontWeight: 'bold'
  },
  brandIcon: {
    fontSize: '1.5rem'
  },
  brandText: {
    fontWeight: '600'
  },
  rightControls: {
    display: 'flex',
    gap: '1rem'
  },
  navButton: {
    background: 'transparent',
    border: '1px solid #fff',
    color: '#fff',
    padding: '0.45rem 1rem',
    borderRadius: '8px',
    fontSize: '0.95rem',
    fontWeight: 500,
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },
  logoutButton: {
    background: '#dc2626',
    border: 'none',
    color: '#fff',
    padding: '0.55rem 1.1rem',
    fontWeight: 600,
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },
  summaryWrap: {
    padding: '2rem 2rem 0',
    display: 'flex',
    justifyContent: 'center'
  },
  summaryGrid: {
    display: 'flex',
    gap: '2rem',
    flexWrap: 'wrap',
    justifyContent: 'center'
  },
  mainContainer: {
    padding: '2rem'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem',
    flexWrap: 'wrap',
    gap: '1rem'
  },
  title: {
    fontSize: '1.8rem',
    fontWeight: 700,
    marginBottom: '0.3rem'
  },
  subTitle: {
    fontSize: '1rem',
    color: '#6b7280'
  },
  actionBtn: {
    background: '#1e3a8a',
    color: '#fff',
    border: 'none',
    padding: '0.7rem 1.4rem',
    borderRadius: '8px',
    fontWeight: 600,
    fontSize: '0.95rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(360px, 1fr))',
    gap: '2rem'
  },
  glassCard: {
    background: 'rgba(255, 255, 255, 0.7)',
    backdropFilter: 'blur(10px)',
    borderRadius: '16px',
    boxShadow: '0 4px 14px rgba(0,0,0,0.08)',
    padding: '1.5rem',
    marginBottom: '1.5rem'
  },
  cardTitle: {
    fontSize: '1.25rem',
    fontWeight: 600,
    marginBottom: '1rem',
    color: '#1f2937',
    borderBottom: '1px solid #e5e7eb',
    paddingBottom: '0.5rem'
  },
  cardBody: {
    fontSize: '0.95rem',
    color: '#374151'
  },
  meta: {
    marginBottom: '0.5rem'
  },
  list: {
    listStyle: 'none',
    padding: 0,
    margin: 0
  },
  listItem: {
    padding: '0.6rem 0',
    borderBottom: '1px solid #e5e7eb',
    fontSize: '0.95rem',
    color: '#374151'
  },
  empty: {
    fontStyle: 'italic',
    color: '#6b7280',
    fontSize: '0.95rem'
  },
  footerNote: {
    fontSize: '0.9rem',
    color: '#6b7280',
    paddingTop: '1rem',
    borderTop: '1px solid #e5e7eb'
  }
};

export default TenantDashboard;
