import React, { useState } from 'react';
import axios from 'axios';

axios.defaults.baseURL = 'http://localhost:5000';

const LeaseForm = ({ onCreate, user }) => {
  const [formData, setFormData] = useState({
    property_id: '',
    tenant_id: '',
    start_date: '',
    end_date: '',
    rent_amount: '',
    status: 'active'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (new Date(formData.end_date) <= new Date(formData.start_date)) {
      return alert('âŒ End date must be after start date');
    }

    try {
      const res = await axios.post('/api/leases', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreate(res.data);
      alert(`âœ… Lease created successfully (ID: ${res.data.lease_id})`);
      setFormData({
        property_id: '',
        tenant_id: '',
        start_date: '',
        end_date: '',
        rent_amount: '',
        status: 'active'
      });
    } catch (err) {
      console.error('âŒ Create lease error:', err);
      alert(err.response?.data?.error || 'Failed to create lease');
    }
  };

  // âœ… Allow both admin and landlord
  if (!user || !['admin', 'landlord'].includes(user.role)) {
    return <p style={{ color: '#c00' }}>ðŸš« Unauthorized to create leases</p>;
  }

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h3>Add New Lease</h3>
      <input name="property_id" placeholder="Property ID" value={formData.property_id} onChange={handleChange} required />
      <input name="tenant_id" placeholder="Tenant ID" value={formData.tenant_id} onChange={handleChange} required />
      <input name="start_date" type="date" value={formData.start_date} onChange={handleChange} required />
      <input name="end_date" type="date" value={formData.end_date} onChange={handleChange} required />
      <input name="rent_amount" type="number" placeholder="Rent Amount" value={formData.rent_amount} onChange={handleChange} required />
      <select name="status" value={formData.status} onChange={handleChange}>
        <option value="active">Active</option>
        <option value="terminated">Terminated</option>
        <option value="pending">Pending</option>
      </select>
      <button type="submit" style={submitButton}>Create Lease</button>
    </form>
  );
};

/* Styles */
const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem',
  marginBottom: '2rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

const submitButton = {
  padding: '0.5rem 1rem',
  backgroundColor: '#0078D4',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  cursor: 'pointer',
  marginTop: '0.5rem'
};

export default LeaseForm;
