import React from 'react';

const LeaseList = ({ leases, onEdit, onDelete, user }) => {
  return (
    <div style={listStyle}>
      <h3>All Leases</h3>
      {leases.length === 0 ? (
        <p>No leases found.</p>
      ) : (
        leases.map((lease) => (
          <div key={lease.lease_id} style={cardStyle}>
            <p><strong>Property:</strong> #{lease.property_id}</p>
            <p><strong>Tenant:</strong> {lease.tenant_name || `#${lease.tenant_id}`}</p>
            <p><strong>Email:</strong> {lease.tenant_email || 'Unknown'}</p>
            <p><strong>Rent:</strong> ${lease.rent_amount}</p>
            <p><strong>Dates:</strong> {lease.start_date} â†’ {lease.end_date}</p>
            <p><strong>Status:</strong> {lease.status}</p>
            {lease.lease_file_url && (
              <p>
                <strong>Lease File:</strong>{' '}
                <a href={lease.lease_file_url} target="_blank" rel="noopener noreferrer">
                  View PDF
                </a>
              </p>
            )}
            {user?.role === 'admin' && (
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <button onClick={() => onEdit(lease)}>Edit</button>
                <button onClick={() => onDelete(lease.lease_id)}>Delete</button>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

const listStyle = {
  marginBottom: '2rem',
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  marginBottom: '1rem',
  backgroundColor: '#f9f9f9',
};

export default LeaseList;
