import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DocumentsForm = ({ leaseId, userId, role }) => {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [replace, setReplace] = useState(false);
  const [loading, setLoading] = useState(false);

  // Fetch documents for this lease
  const fetchDocs = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`/api/documents/${leaseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDocs(res.data);
    } catch (err) {
      console.error('❌ Fetch error:', err);
    }
  };

  useEffect(() => {
    if (leaseId) fetchDocs();
  }, [leaseId]);

  // Upload new document
  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('lease_id', leaseId);
    formData.append('uploaded_by', userId);
    formData.append('replace', replace);
    formData.append('file', file);

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/documents/upload', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFile(null);
      setReplace(false);
      fetchDocs();
    } catch (err) {
      console.error('❌ Upload error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Delete document
  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/documents/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchDocs();
    } catch (err) {
      console.error('❌ Delete error:', err);
    }
  };

  // Toggle tenant visibility
  const handleToggle = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/documents/visibility/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchDocs();
    } catch (err) {
      console.error('❌ Toggle error:', err);
    }
  };

  // Role-based filtering
  const visibleDocs = role === 'tenant'
    ? docs.filter(d => d.is_visible_to_tenant)
    : docs;

  return (
    <div>
      <h3>Lease Documents</h3>

      {role !== 'tenant' && (
        <div style={{ marginBottom: '1rem' }}>
          <input type="file" onChange={e => setFile(e.target.files[0])} />
          <label>
            <input
              type="checkbox"
              checked={replace}
              onChange={e => setReplace(e.target.checked)}
            />
            Replace existing
          </label>
          <button onClick={handleUpload} disabled={loading}>
            {loading ? 'Uploading...' : 'Upload'}
          </button>
        </div>
      )}

      <ul>
        {visibleDocs.map(doc => (
          <li key={doc.document_id}>
            <a href={doc.file_url} target="_blank" rel="noopener noreferrer">
              {doc.file_url.split('/').pop()}
            </a>
            {role !== 'tenant' && (
              <>
                <button onClick={() => handleDelete(doc.document_id)}>Delete</button>
                <button onClick={() => handleToggle(doc.document_id)}>
                  {doc.is_visible_to_tenant ? 'Hide from tenant' : 'Show to tenant'}
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DocumentsForm;
