import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SmartPasscodeForm from './SmartPasscodeForm';
import SmartPasscode from './SmartPasscode';

const PasscodesPage = () => {
  const [leases, setLeases] = useState([]);
  const [users, setUsers] = useState([]);
  const [passcodes, setPasscodes] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = token ? { Authorization: `Bearer ${token}` } : undefined;

        const leasesRes = await axios.get('/api/leases', { headers });
        const usersRes = await axios.get('/api/passcodes/users-with-leases', { headers });

        setLeases(leasesRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        console.error('❌ Fetch error:', err);
        setError('Failed to load leases or users.');
      }
    };

    fetchData();
  }, []);

  const handleRevoke = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/passcodes/${id}/revoke`, {}, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.map(p => p.passcode_id === id ? { ...p, is_active: false } : p));
    } catch (err) {
      console.error('❌ Revoke error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('❌ Delete error:', err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Smart Passcodes</h2>

      <SmartPasscodeForm
        leases={leases}
        users={users}
        onCreated={(newPasscode) => setPasscodes([...passcodes, newPasscode])}
      />

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <div style={{ marginTop: '2rem' }}>
        <h3>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p>No active passcodes yet.</p>
        ) : (
          <div style={{ display: 'grid', gap: '1rem' }}>
            {passcodes.map((p) => (
              <SmartPasscode
                key={p.passcode_id}
                passcode={p}
                onRevoke={handleRevoke}
                onDelete={handleDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PasscodesPage;
