import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PropertyForm from '../components/PropertyForm';
import PropertyList from '../components/PropertyList';

const AdminProperties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    async function fetchProperties() {
      try {
        const res = await axios.get('/api/properties', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setProperties(res.data);
      } catch (err) {
        console.error('‚ùå Fetch error:', err);
      } finally {
        setLoading(false);
      }
    }
    fetchProperties();
  }, []);

  const handleCreate = (newProp) => {
    setProperties(prev => [newProp, ...prev]);
  };

  const handleDelete = (id) => {
    setProperties(prev => prev.filter(p => p.property_id !== id));
  };

  const handleUpdate = (updatedProp) => {
    setProperties(prev =>
      prev.map(p => (p.property_id === updatedProp.property_id ? updatedProp : p))
    );
  };

  if (!user || user.role !== 'admin') {
    return <p style={unauthorizedStyle}>üö´ Unauthorized access</p>;
  }

  if (loading) {
    return <p style={loadingStyle}>Loading properties...</p>;
  }

  return (
    <div style={pageStyle}>
      <h2 style={headingStyle}>üè† Manage Properties</h2>
      <div style={divider}></div>

      <div style={formWrapper}>
        <PropertyForm onCreate={handleCreate} user={user} />
      </div>

      <div style={listWrapper}>
        <PropertyList
          properties={properties}
          onDelete={handleDelete}
          onUpdate={handleUpdate}
          user={user}
        />
      </div>
    </div>
  );
};

/* Styles */
const pageStyle = {
  padding: '2rem',
  fontFamily: 'Segoe UI, sans-serif',
  backgroundColor: '#f4f6f8',
  minHeight: '100vh'
};

const headingStyle = {
  fontSize: '2rem',
  marginBottom: '0.5rem',
  color: '#333',
  textAlign: 'center'
};

const divider = {
  borderBottom: '2px solid #ddd',
  margin: '0 auto 2rem',
  width: '80%'
};

const formWrapper = {
  marginBottom: '2rem',
  backgroundColor: '#fff',
  padding: '1.5rem',
  borderRadius: '10px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
};

const listWrapper = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
  gap: '1.5rem'
};

const loadingStyle = {
  textAlign: 'center',
  fontSize: '1.2rem',
  marginTop: '3rem',
  color: '#555'
};

const unauthorizedStyle = {
  textAlign: 'center',
  fontSize: '1.2rem',
  marginTop: '3rem',
  color: '#c00'
};

export default AdminProperties;
