import React, { useState } from 'react';
import axios from 'axios';
import PropertyEditForm from './PropertyEditForm';

const PropertyList = ({ properties, onDelete, onUpdate }) => {
  const [editingId, setEditingId] = useState(null);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/properties/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onDelete(id);
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  const handleUpdate = (updatedProp) => {
    onUpdate(updatedProp);
    setEditingId(null);
  };

  return (
    <div style={listStyle}>
      <h3>All Properties</h3>
      {properties.length === 0 ? (
        <p>No properties found.</p>
      ) : (
        properties.map(prop => (
          <div key={prop.property_id} style={cardStyle}>
            {editingId === prop.property_id ? (
              <PropertyEditForm
                property={prop}
                onUpdate={handleUpdate}
                onCancel={() => setEditingId(null)}
              />
            ) : (
              <>
                <p><strong>Address:</strong> {prop.address}, {prop.city}, {prop.state} {prop.zip}</p>
                <p><strong>Rent:</strong> ${prop.rent_amount}</p>
                <p><strong>Status:</strong> {prop.status}</p>
                <p><strong>Owner:</strong> User #{prop.user_id}</p>
                <button onClick={() => setEditingId(prop.property_id)}>Edit</button>
                <button onClick={() => handleDelete(prop.property_id)}>Delete</button>
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

const listStyle = {
  padding: '1rem',
  backgroundColor: '#fff',
  borderRadius: '10px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
};

const cardStyle = {
  marginBottom: '1rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

export default PropertyList;
