import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  // Load user + reminders
  useEffect(() => {
    const stored = localStorage.getItem('user');
    if (stored) {
      const parsed = JSON.parse(stored);
      setUser(parsed);

      if (parsed.role !== 'tenant') {
        navigate('/unauthorized');
      }

      api.get(`/user-reminders/${parsed.user_id}`).then((res) => setReminders(res.data));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Load lease
  useEffect(() => {
    if (!user?.user_id) return;
    const loadLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get(`/leases/user/${user.user_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLease(res.data);
      } catch (err) {
        console.error("‚ùå Error loading lease:", err.response?.data || err.message);
      }
    };
    loadLease();
  }, [user]);

  // Load passcodes
  useEffect(() => {
    if (lease?.lease_id) {
      api.get(`/smart-passcodes/${lease.lease_id}`).then((res) => setPasscodes(res.data));
    }
  }, [lease]);

  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post(
        '/payments/create-session',
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      console.error('‚ùå Payment error:', err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  return (
    <div style={styles.page}>
      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Tenant Portal</div>
        <div style={styles.navRight}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>
              Profile
            </button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>
              Dashboard
            </button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {/* SUMMARY BAR */}
      {lease && (
        <div style={styles.summaryBar}>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Next Rent Due</span>
            <span style={styles.summaryValue}>${lease.rent_amount}</span>
          </div>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Lease Ends</span>
            <span style={styles.summaryValue}>
              {new Date(lease.end_date).toLocaleDateString()}
            </span>
          </div>
          <div style={styles.summaryItem}>
            <span style={styles.summaryLabel}>Payments Made</span>
            <span style={styles.summaryValue}>{/* could count payments here */}</span>
          </div>
        </div>
      )}

      {/* MAIN CONTENT */}
      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name || 'Guest'}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* PROFILE CARD */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
        </div>

        {/* LEASE INFO */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Lease Info</h3>
          <TenantLeaseCard lease={lease} user={user} />
        </div>

        {/* PAYMENTS */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Payment History</h3>
          <TenantPayments userId={user?.user_id} />
          <button style={styles.button} onClick={handlePaymentClick}>Make a Payment</button>
        </div>

        {/* DOCUMENTS */}
        {lease && (
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>Lease Photos & Documents</h3>
            <LeasePhotosForm leaseId={lease.lease_id} token={localStorage.getItem('token')} role={user?.role} />
            <DocumentsForm leaseId={lease.lease_id} userId={user?.user_id} role={user?.role} />
          </div>
        )}

        {/* REMINDERS */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Reminders</h3>
          {reminders.length === 0 ? (
            <p style={{ color: '#888' }}>No reminders yet üéâ</p>
          ) : (
            <ul style={styles.list}>
              {reminders.map((r) => (
                <li key={r.reminder_id}>
                  <strong>{r.type}:</strong> {r.message} ‚Äî {new Date(r.remind_at).toLocaleDateString()}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* PASSCODES */}
        {lease && (
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>Smart Passcodes</h3>
            {passcodes.length === 0 ? (
              <p style={{ color: '#888' }}>No active passcodes</p>
            ) : (
              <ul style={styles.list}>
                {passcodes.map((pc) => (
                  <li key={pc.passcode_id}>
                    Code: {pc.passcode} ‚Äî Expires: {new Date(pc.expires_at).toLocaleString()}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

