/* ---------- TenantDashboard UI Update ---------- */
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = useCallback(() => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    if (onLogout) onLogout();
    navigate("/login");
  }, [navigate, onLogout]);

  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (!stored) return;
    const parsed = JSON.parse(stored);
    setUser(parsed);
    if (parsed.role !== "tenant") navigate("/unauthorized");

    api.get(`/user-reminders/${parsed.user_id}`)
      .then(res => setReminders(res.data))
      .catch(err => {
        const msg = err.response?.data?.message;
        if (msg === "Token expired") handleLogout();
        else console.error("‚ùå Error loading reminders:", msg || err.message);
      });
  }, [navigate, handleLogout]);

  useEffect(() => {
    if (!user?.user_id) return;
    const loadLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get(`/leases/user/${user.user_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLease(res.data.lease || res.data);
      } catch (err) {
        const msg = err.response?.data?.message;
        if (msg === 'Token expired') handleLogout();
        else console.error("‚ùå Error loading lease:", msg || err.message);
      }
    };
    loadLease();
  }, [user, handleLogout]);

  useEffect(() => {
    if (lease?.lease_id) {
      api.get(`/smart-passcodes/${lease.lease_id}`)
        .then(res => setPasscodes(res.data))
        .catch(err => {
          const msg = err.response?.data?.message;
          if (msg === 'Token expired') handleLogout();
          else console.error("‚ùå Error loading passcodes:", msg || err.message);
        });
    }
  }, [lease, handleLogout]);

  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post(
        '/payments/create-session',
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === 'Token expired') handleLogout();
      else console.error('‚ùå Payment error:', msg || err.message);
    }
  };

  const accent = '#3B82F6'; // neon-blue accent

  return (
    <div style={styles.page}>
      {/* NAVBAR */}
      <nav style={{ ...styles.navbar, boxShadow: '0 8px 30px rgba(2,6,23,0.6)' }}>
        <div style={styles.brand}>
          <div style={styles.brandIcon}>üè†</div>
          <div style={styles.brandText}>Tenant Portal</div>
        </div>

        <div style={styles.rightControls}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>Profile</button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>Dashboard</button>
          )}
          <button
            onClick={handleLogout}
            style={{ ...styles.logoutButton, background: 'linear-gradient(90deg,#ef4444,#dc2626)' }}
          >
            Logout
          </button>
        </div>
      </nav>

      {/* SUMMARY (glass cards) */}
      <section style={styles.summaryWrap}>
        <div style={styles.summaryGrid}>
          <CardGlass label="Next Rent Due" icon="üí∞" value={
            lease && parseFloat(lease.rent_amount || 0) > 0
              ? `$${parseFloat(lease.rent_amount).toFixed(2)}`
              : 'Not set'
          } accent={accent} />
          <CardGlass label="Lease Ends" icon="üìÖ" value={
            lease?.end_date ? new Date(lease.end_date).toLocaleDateString() : '‚Äî'
          } accent={accent} />
          <CardGlass label="Payments Made" icon="üí≥" value={
            lease?.rent_amount && passcodes.length > 0
              ? `$${(passcodes.length * parseFloat(lease.rent_amount)).toFixed(2)}`
              : '‚Äî'
          } accent={accent} />
        </div>
      </section>

      {/* MAIN GRID */}
      <main style={styles.mainContainer}>
        <header style={styles.header}>
          <div>
            <h1 style={styles.title}>Welcome, <span style={{ color: accent }}>{user?.name ?? 'Guest'}</span></h1>
            <div style={styles.subTitle}>Role: <span style={{ color: '#cbd5e1', fontWeight: 600 }}>{user?.role}</span></div>
          </div>
          <div style={styles.headerActions}>
            <button
              onClick={handlePaymentClick}
              style={{ ...styles.actionBtn, background: accent, boxShadow: `0 6px 18px ${hexToRgba(accent, 0.18)}` }}
            >
              Make Payment
            </button>
          </div>
        </header>

        <section style={styles.grid}>
          {/* LEFT COLUMN */}
          <div>
            <GlassCard title="üë§ Tenant Profile">
              <p style={styles.meta}><strong>Name:</strong> {user?.name ?? '‚Äî'}</p>
              <p style={styles.meta}><strong>Email:</strong> {user?.email ?? '‚Äî'}</p>
            </GlassCard>

            <GlassCard title="üìÑ Lease Information">
              <TenantLeaseCard lease={lease} user={user} showTitle={false} />
            </GlassCard>

            <GlassCard title="üñº Lease Photos">
              <LeasePhotosForm
                leaseId={lease?.lease_id}
                token={localStorage.getItem('token')}
                role={user?.role}
                showTitle={false}
              />
            </GlassCard>

            <GlassCard title="üìÅ Lease Documents">
              <DocumentsForm
                leaseId={lease?.lease_id}
                userId={user?.user_id}
                role={user?.role}
                showTitle={false}
              />
            </GlassCard>
          </div>

          {/* RIGHT COLUMN */}
          <div>
            <GlassCard title="üí≥ Payment History">
              <TenantPayments userId={user?.user_id} />
            </GlassCard>

            <GlassCard title="üîî Reminders">
              {reminders.length === 0 ? (
                <p style={styles.empty}>No reminders yet üéâ</p>
              ) : (
                <ul style={styles.list}>
                  {reminders.map((r) => (
                    <li key={r.reminder_id} style={styles.listItem}>
                      <strong>{r.type}:</strong> {r.message} ‚Äî {new Date(r.remind_at).toLocaleDateString()}
                    </li>
                  ))}
                </ul>
              )}
            </GlassCard>

            <GlassCard title="üîê Smart Passcodes">
              {passcodes.length === 0 ? (
                <p style={styles.empty}>No active passcodes</p>
              ) : (
                <ul style={styles.list}>
                  {passcodes.map((pc) => (
                    <li key={pc.passcode_id} style={styles.listItem}>
                      <strong>Code:</strong> {pc.passcode} ‚Äî Expires: {new Date(pc.expires_at).toLocaleString()}
                    </li>
                  ))}
                </ul>
              )}
            </GlassCard>

            <div style={{ height: 12 }} /> {/* small spacer */}
            <div style={{ ...styles.footerNote, borderTop: '1px solid rgba(255,255,255,0.04)' }}>
              <strong>Support:</strong> Contact your property manager for questions or maintenance requests.
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

/* ---------- Reusable GlassCard Component ---------- */
const GlassCard = ({ title, children }) => (
  <div style={{
    background: 'linear-gradient(180deg, rgba(255,255,255,0.02), rgba(255,255,255,0.01))',
    border: '1px solid rgba(255,255,255,0.05)',
    borderRadius: 16,
    padding: 20,
    marginBottom: 16,
    backdropFilter: 'blur(12px) saturate(180%)',
    boxShadow: '0 10px 28px rgba(2,6,23,0.7), inset 0 1px 0 rgba(255,255,255,0.02)'
  }}>
    <div style={{
      fontSize: 16,
      fontWeight: 700,
      color: '#e6eef8',
      marginBottom: 12,
      display: 'flex',
      alignItems: 'center',
      gap: 8,
      borderBottom: '1px solid rgba(255,255,255,0.03)',
      paddingBottom: 8
    }}>
      {title}
    </div>
    <div>{children}</div>
  </div>
);

/* ---------- Small Helper Component & Functions ---------- */
function CardGlass({ label, icon, value, accent }) {
  return (
    <div style={{
      background: 'linear-gradient(180deg, rgba(238, 229, 229, 0.04), rgba(255,255,255,0.02))',
      border: '1px solid rgba(255,255,255,0.06)',
      borderRadius: 16,
      padding: '20px 24px',
      minWidth: 200,
      textAlign: 'center',
      boxShadow: `0 8px 32px rgba(2,6,23,0.5), inset 0 1px 0 rgba(255,255,255,0.02)`,
      transition: 'transform 0.2s',
    }}>
      <div style={{ fontSize: 26 }}>{icon}</div>
      <div style={{ color: '#cbd5e1', fontWeight: 600, marginTop: 8 }}>{label}</div>
      <div style={{ fontSize: 20, marginTop: 6, fontWeight: 700, color: accent }}>{value}</div>
    </div>
  );
}

function hexToRgba(hex, alpha = 1) {
  const h = hex.replace('#', '');
  const bigint = parseInt(h.length === 3 ? h.split('').map(c => c + c).join('') : h, 16);
  const r = (bigint >> 16) & 255;
  const g = (bigint >> 8) & 255;
  const b = bigint & 255;
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

/* ---------- Styles ---------- */
const styles = {
  page: {
    background: 'linear-gradient(180deg,#071033 0%, #0b1220 55%, #071033 100%)',
    minHeight: '100vh',
    color: '#e6eef8',
    fontFamily: "'Inter', system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial",
    paddingBottom: 40
  },
  navbar: {
    height: 72,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '0 28px',
    borderBottom: '1px solid rgba(255,255,255,0.03)',
    backdropFilter: 'saturate(140%) blur(6px)'
  },
  brand: { display: 'flex', alignItems: 'center', gap: 12 },
  brandIcon: { fontSize: 20 },
  brandText: { fontSize: 18, fontWeight: 700, letterSpacing: 0.2 },
  rightControls: { display: 'flex', alignItems: 'center', gap: 10 },
  navButton: {
    background: 'transparent',
    border: '1px solid rgba(255,255,255,0.06)',
    color: '#c7d2fe',
    padding: '8px 12px',
    borderRadius: 8,
    cursor: 'pointer',
    fontWeight: 600,
    fontSize: 14,
    transition: 'all 0.2s',
  },
  logoutButton: {
    padding: '8px 14px',
    borderRadius: 8,
    color: '#fff',
    fontWeight: 700,
    cursor: 'pointer',
    border: 'none'
  },
  summaryWrap: { display: 'flex', justifyContent: 'center', padding: '28px 20px 8px' },
  summaryGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 18, width: '100%', maxWidth: 1100, alignItems: 'stretch' },
  mainContainer: { width: '100%', maxWidth: 1200, margin: '18px auto 0', padding: '0 20px' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 18 },
  title: { fontSize: 26, margin: 0, fontWeight: 800 },
  subTitle: { color: '#94a3b8', marginTop: 6 },
  headerActions: {},
  actionBtn: {
    padding: '10px 16px',
    borderRadius: 10,
    color: '#fff',
    fontWeight: 700,
    border: 'none',
    cursor: 'pointer',
    fontSize: 14,
    transition: 'all 0.2s',
  },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: 22, alignItems: 'start' },
  meta: { color: '#cbd5e1', margin: '6px 0' },
  list: { margin: 0, padding: 0, listStyle: 'none' },
  listItem: { padding: '8px 0', borderBottom: '1px dashed rgba(255,255,255,0.03)', color: '#cbd5e1' },
  empty: { color: '#94a3b8', fontStyle: 'italic' },
  footerNote: { color: '#9fb0d6', padding: '14px 18px', borderRadius: 12, background: 'linear-gradient(180deg, rgba(255,255,255,0.01), rgba(255,255,255,0.008))' }
};

export default TenantDashboard;
