import React, { useState } from "react";
import axios from "axios";

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState("");
  const [userId, setUserId] = useState("");
  const [expiresAt, setExpiresAt] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    const lease_id = Number(leaseId);
    const user_id = Number(userId);

    if (!lease_id || !user_id) {
      setError("Please select valid Lease and User.");
      return;
    }

    try {
      const res = await axios.post(
        "/api/smart-passcodes",
        {
          lease_id,
          user_id,
          expires_at: expiresAt || null,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      onCreated(res.data.passcode);
      setLeaseId("");
      setUserId("");
      setExpiresAt("");
      setSuccess("Passcode generated successfully!");
    } catch (err) {
      console.error("Create error:", err);
      setError("Failed to generate passcode. Please try again.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white p-6 rounded-xl shadow-sm border border-gray-200 space-y-6"
    >
      <h3 className="text-xl font-semibold">Generate Smart Passcode</h3>

      {/* Lease + User */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium mb-1">Lease</label>
          <select
            value={leaseId}
            onChange={(e) => setLeaseId(e.target.value)}
            className="w-full border border-gray-300 rounded-lg px-3 py-2 text-sm focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select Lease</option>
            {leases
              .sort((a, b) => a.lease_id - b.lease_id)
              .map((l) => (
                <option key={l.lease_id} value={l.lease_id}>
                  Lease #{l.lease_id} â€“ Property {l.property_id}
                </option>
              ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">User</label>
          <select
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            className="w-full border border-gray-300 rounded-lg px-3 py-2 text-sm focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select User</option>
            {users
              .sort((a, b) => a.user_id - b.user_id)
              .map((u) => (
                <option key={u.user_id} value={u.user_id}>
                  {u.name || `User #${u.user_id}`}
                </option>
              ))}
          </select>
        </div>
      </div>

      {/* Expiry */}
      <div>
        <label className="block text-sm font-medium mb-1">Expiry (optional)</label>
        <input
          type="datetime-local"
          value={expiresAt}
          onChange={(e) => setExpiresAt(e.target.value)}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 text-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {/* Button */}
      <button
        type="submit"
        className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-lg text-sm font-semibold"
      >
        Generate Passcode
      </button>

      {/* Messages */}
      {error && <div className="text-red-600 text-sm">{error}</div>}
      {success && <div className="text-green-600 text-sm">{success}</div>}
    </form>
  );
};

export default SmartPasscodeForm;
