import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const lease_id = Number(leaseId);
    const user_id = Number(userId);
    if (!lease_id || !user_id) {
      setError('Please select valid Lease and User.');
      return;
    }

    try {
      const res = await axios.post('/api/smart-passcodes', {
        lease_id,
        user_id,
        expires_at: expiresAt || null
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
      setError('');
    } catch {
      setError('Failed to generate passcode.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
      <h3>Generate Smart Passcode</h3>

      <select value={leaseId} onChange={(e) => setLeaseId(e.target.value)}>
        <option value="">Select Lease</option>
        {leases.map((l, i) => (
          <option key={l.lease_id ?? i} value={String(l.lease_id ?? '')}>
            Lease #{l.lease_id ?? i} â€“ Property {l.property_id ?? ''}
          </option>
        ))}
      </select>

      <select value={userId} onChange={(e) => setUserId(e.target.value)}>
        <option value="">Select User</option>
        {users.map((u, i) => (
          <option key={u.user_id ?? i} value={String(u.user_id ?? '')}>
            {u.name || `User #${u.user_id ?? i}`}
          </option>
        ))}
      </select>

      <input
        type="datetime-local"
        value={expiresAt}
        onChange={(e) => setExpiresAt(e.target.value)}
      />

      <button type="submit">Generate Passcode</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
};

export default SmartPasscodeForm;
