import React from "react";

const SmartPasscode = ({ passcode, onRevoke, onDelete }) => {
  const {
    passcode_id,
    passcode: code,
    is_active,
    expires_at,
    lease_id,
    user_id,
  } = passcode;

  const displayCode = code ? code.toString().padStart(4, "0") : "----";
  const status = is_active ? "Active" : "Revoked";
  const statusColor = is_active ? "bg-green-500" : "bg-red-500";

  const formattedExpiry = expires_at
    ? new Date(expires_at).toLocaleString()
    : "No expiry set";

  return (
    <div className="bg-white border border-gray-200 shadow-sm rounded-xl p-5 flex flex-col gap-4 hover:shadow-md transition">
      
      {/* Header */}
      <div className="flex items-center justify-between">
        <span className="text-xl font-semibold flex items-center gap-2">
          ðŸ”‘ {displayCode}
        </span>

        <span className={`${statusColor} text-white text-xs px-3 py-1 rounded-full`}>
          {status}
        </span>
      </div>

      {/* Details */}
      <div className="text-sm text-gray-600 leading-relaxed">
        <div><strong>Lease:</strong> #{lease_id}</div>
        <div><strong>User:</strong> #{user_id}</div>
        <div><strong>Expires:</strong> {formattedExpiry}</div>
      </div>

      {/* Actions */}
      <div className="flex gap-3 mt-2">
        {is_active && (
          <button
            onClick={() => onRevoke(passcode_id)}
            className="bg-yellow-500 hover:bg-yellow-600 text-white text-sm px-4 py-2 rounded-lg font-medium"
          >
            Revoke
          </button>
        )}

        <button
          onClick={() => onDelete(passcode_id)}
          className="bg-red-500 hover:bg-red-600 text-white text-sm px-4 py-2 rounded-lg font-medium"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default SmartPasscode;
