import React, { useState } from 'react';

const MaintenanceList = ({ requests, onUpdate, onDelete, user }) => {
  const [statusFilter, setStatusFilter] = useState('all');
  const [sortOrder, setSortOrder] = useState('desc');

  const handleStatusChange = (id, status) => {
    onUpdate(id, { status });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      onDelete(id);
    }
  };

  const badgeStyle = (status) => {
    const colors = {
      open: '#f39c12',
      in_progress: '#3498db',
      closed: '#2ecc71'
    };
    return {
      backgroundColor: colors[status] || '#ccc',
      color: '#fff',
      padding: '0.25rem 0.5rem',
      borderRadius: '4px',
      fontSize: '0.8rem',
      textTransform: 'capitalize'
    };
  };

  const filtered = requests.filter(r =>
    statusFilter === 'all' ? true : r.status === statusFilter
  );

  const sorted = [...filtered].sort((a, b) => {
    const timeA = new Date(a.created_at).getTime();
    const timeB = new Date(b.created_at).getTime();
    return sortOrder === 'asc' ? timeA - timeB : timeB - timeA;
  });

  return (
    <div style={{ marginTop: '2rem' }}>
      <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="all">All Statuses</option>
          <option value="open">Open</option>
          <option value="in_progress">In Progress</option>
          <option value="closed">Closed</option>
        </select>
        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="desc">Newest First</option>
          <option value="asc">Oldest First</option>
        </select>
      </div>

      {sorted.length === 0 ? (
        <p>No matching maintenance requests.</p>
      ) : (
        sorted.map((r) => (
          <div key={r.request_id} style={card}>
            <div><strong>Property:</strong> #{r.property_id}</div>
            <div><strong>Tenant:</strong> #{r.user_id}</div>
            <div><strong>Description:</strong> {r.description}</div>
            <div><strong>Status:</strong> <span style={badgeStyle(r.status)}>{r.status}</span></div>
            <div><strong>Created:</strong> {new Date(r.created_at).toLocaleString()}</div>

            {['admin','landlord'].includes(user?.role) && (
              <div style={actions}>
                {r.status !== 'in_progress' && (
                  <button onClick={() => handleStatusChange(r.request_id, 'in_progress')}>Mark In Progress</button>
                )}
                {r.status !== 'closed' && (
                  <button onClick={() => handleStatusChange(r.request_id, 'closed')}>Close</button>
                )}
                <button onClick={() => handleDelete(r.request_id)}>Delete</button>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

const card = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  marginBottom: '1rem',
  backgroundColor: '#f9f9f9'
};

const actions = {
  display: 'flex',
  gap: '0.5rem',
  marginTop: '0.5rem'
};

export default MaintenanceList;
